INSERT INTO `txp_css` (`name`, `css`) VALUES
('default', '/* base\r\n----------------------------------------------- */\r\n\r\nbody {\r\n	margin: 0;\r\n	padding: 0;\r\n	font-family: Verdana, "Lucida Grande", Tahoma, Helvetica, sans-serif;\r\n	color: #000;\r\n	background-color: #fff;\r\n}\r\n\r\nblockquote, h3, p, li {\r\n	padding-right: 10px;\r\n	padding-left: 10px;\r\n	font-size: 0.9em;\r\n	line-height: 1.6em;\r\n}\r\n\r\nblockquote {\r\n	margin-right: 0;\r\n	margin-left: 20px;\r\n}\r\n\r\nh1, h2, h3 {\r\n	margin: 0 0 15px 0;\r\n	padding: 0 10px;\r\n	font-weight: normal;\r\n}\r\n\r\nh1, h2 {\r\n	font-family: Georgia, Times, serif;\r\n}\r\n\r\nh1 {\r\n	font-size: 1.4em;\r\n}\r\n\r\nh2 {\r\n	font-size: 1em;\r\n	font-style: italic;\r\n}\r\n\r\nhr {\r\n	margin: 2em auto;\r\n	width: 370px;\r\n	height: 1px;\r\n	color: #7a7e7d;\r\n	background-color: #7a7e7d;\r\n	border: none;\r\n}\r\n\r\nsmall, .small {\r\n	font-size: 0.9em;\r\n}\r\n\r\n/* links\r\n----------------------------------------------- */\r\n\r\na {\r\n	text-decoration: none;\r\n	color: #000;\r\n	border-bottom: 1px #000 solid;\r\n}\r\n\r\na img {\r\n	border: none;\r\n}\r\n\r\nh1 a, h2 a, h3 a {\r\n	border: none;\r\n}\r\n\r\nh3 a {\r\n	font: 1.5em Georgia, Times, serif;\r\n}\r\n\r\n#site-name a {\r\n	border: none;\r\n}\r\n\r\n#sidebar-2 a, #sidebar-1 a {\r\n	color: #c00;\r\n	border: none;\r\n}\r\n\r\n/* layout\r\n----------------------------------------------- */\r\n\r\n#accessibility {\r\n	position: absolute;\r\n	top: -10000px;\r\n}\r\n\r\n#container {\r\n	margin: 10px auto;\r\n	padding: 10px;\r\n	width: 760px;\r\n}\r\n\r\n#head {\r\n	text-align: center;\r\n}\r\n\r\n#site-name {\r\n	margin: 15px 0;\r\n	font: 3em Georgia, Times, serif;\r\n}\r\n\r\n#site-slogan {\r\n	font: italic 1em Georgia, Times, serif;\r\n}\r\n\r\n#sidebar-1, #sidebar-2 {\r\n	padding-top: 50px;\r\n	width: 150px;\r\n}\r\n\r\n#sidebar-1 {\r\n	margin-right: 5px;\r\n	float: left;\r\n	text-align: right;\r\n}\r\n\r\n#sidebar-2 {\r\n	margin-left: 5px;\r\n	float: right;\r\n}\r\n\r\n.section_list {\r\n	margin: 0 0 10px 0;\r\n	padding: 0;\r\n	list-style-type: none;\r\n}\r\n\r\n.section_list ul {\r\n	list-style-type: none;\r\n}\r\n\r\n.section_list li {\r\n	margin: 0 10px 2px 0;\r\n	padding: 0;\r\n}\r\n\r\n#content {\r\n	margin: 0 155px;\r\n	padding-top: 30px;\r\n}\r\n\r\n#foot {\r\n	margin-top: 5px;\r\n	clear: both;\r\n	text-align: center;\r\n}\r\n\r\n/* box model hacks\r\nhttp://archivist.incutio.com/viewlist/css-discuss/48386\r\n----------------------------------------------- */\r\n\r\n#container {\r\n\\width: 770px;\r\nw\\idth: 760px;\r\n}\r\n\r\n#sidebar-1, #sidebar-2 {\r\n\\width: 150px;\r\nw\\idth: 150px;\r\n}\r\n\r\n/* overrides\r\n----------------------------------------------- */\r\n\r\n#sidebar-2 p, #sidebar-1 p {\r\n	font-size: 0.8em;\r\n	line-height: 1.5em;\r\n}\r\n\r\n.caps {\r\n	font-size: 0.9em;\r\n	letter-spacing: 0.1em;\r\n}\r\n\r\ndiv.divider {\r\n	margin: 2em 0;\r\n	text-align: center;\r\n}\r\n\r\n/* articles\r\n----------------------------------------------- */\r\n\r\n.directory {\r\n	list-style-type: circle;\r\n}\r\n\r\n.author {\r\n	font-style: normal;\r\n	font-size: 0.8em;\r\n}\r\n\r\n.published {\r\n	font-size: 0.8em;\r\n}\r\n\r\n/* comments\r\n----------------------------------------------- */\r\n\r\n.comments_error {\r\n	color: #000;\r\n	background-color: #fff4f4;\r\n}\r\n\r\nul.comments_error {\r\n	padding : 0.3em;\r\n	list-style-type: circle;\r\n	list-style-position: inside;\r\n	border: 2px solid #fdd;\r\n}\r\n\r\ndiv#cpreview {\r\n	color: #000;\r\n	background-color: #f1f1f1;\r\n	border: 2px solid #ddd;\r\n}\r\n\r\nform#txpCommentInputForm td {\r\n	vertical-align: top;\r\n}\r\n\r\n#comments-help {\r\n	margin: 2px 0 15px 0;\r\n	font-size: 0.7em;\r\n}\r\n\r\n/* error page\r\n----------------------------------------------- */\r\n\r\n.error-status {\r\n	font: 1.3em Georgia, Times, serif;\r\n}');
