	If $vars is an array, use url() to expand as an GET style url and redirect to\n		that location using the HTTP status code definition defined by $status.\n		*/\n\n		static $status_definitions = array (\n			301 => "Moved Permanently",\n			302 => "Found",\n			303 => "See Other",\n			307 => "Temporary Redirect"\n		);\n\n		if (!in_array($status, array_keys($status_definitions)))\n			$status = 303;\n\n		if (is_array($url))\n			$url = $this->url($url);\n		else {\n			$url_details = parse_url($url);\n			if (!@$url_details[''scheme''])\n				$url = ''http://''.$url;\n		}\n\n		if (empty($_SERVER[''FCGI_ROLE'']) and empty($_ENV[''FCGI_ROLE''])) {\n			header(''HTTP/1.1 ''.$status.'' ''.$status_definitions[$status]);\n			header(''Status: ''.$status);\n			header(''Location: ''.$url);\n			header(''Connection: close'');\n			header(''Content-Length: 0'');\n			exit(0);\n			} else {\n			global $sitename;\n			$url = htmlspecialchars($url);\n			echo <<<END\n			<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\n			<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\n			<head>\n				<title>$sitename</title>\n				<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />\n				<meta http-equiv="refresh" content="0;url=$url" />\n			</head>\n			<body>\n			<a href="$url">{$status_definitions[$status]}</a>\n			</body>\n			</html>\nEND;\n		}\n	}\n\n	function pref ($key) {\n		global $prefs, $txp_current_plugin;\n\n		$plugin = ($this->plugin_name) ? $this->plugin_name : $txp_current_plugin;\n		$key = $plugin.''_''.$key;\n\n		if (@$this->preferences[$key])\n			return $this->preferences[$key][''value''];\n		if (@$prefs[$key])\n			return $prefs[$key];\n		return NULL;\n	}\n}\n\nclass GBPAdminTabView {\n	//	Internal variables\n	var $title;\n	var $event;\n	var $is_active;\n	var $parent;\n	var $permissions = ''1,2,3,4,5,6'';\n\n	//	Constructor\n	function GBPAdminTabView ($title, $event, &$parent, $is_default = NULL) {\n\n		$this->title = (function_exists(''mb_convert_case''))\n			? mb_convert_case($title, MB_CASE_TITLE, "UTF-8")\n			: ucwords($title);\n\n		$this->event = $event;\n\n		// Note: $this->parent only gets set correctly for PHP 5\n		$this->parent =& $parent->add_tab($this, $is_default);\n\n		// Add privs for this tab\n		global $txp_permissions;\n		$perms = @$txp_permissions[$this->permissions];\n		add_privs($this->parent->event.''.''.$this->event, ($perms ? $perms : $this->permissions));\n	}\n\n	function php_4_fix () {\n\n		// Fix references in PHP 4 so sub tabs can access their parent tab\n		if (version_compare(phpversion(), ''5.0.0'', ''<'')) {\n			global $txp_current_plugin, $gbp_admin_lib_refs;\n			$this->parent =& $gbp_admin_lib_refs[$txp_current_plugin];\n		}\n	}\n\n	function preload () {\n		// Override this function\n	}\n\n	function render_tab () {\n\n		// Grab the url to this tab\n		$url = $this->parent->url(array(gbp_tab => $this->event), true);\n\n		// Will need updating if any improvements happen to the admin interface\n		$out[] = ''<td class="'' . ($this->is_active ? ''tabup'' : ''tabdown2'');\n		$out[] = ''" onclick="window.location.href=\\'''' .$url. ''\\''">'';\n		$out[] = ''<a href="'' .$url. ''" class="plain">'' .$this->title. ''</a></td>'';\n\n		return join('''', $out);\n	}\n\n	function main () {\n		// Override this function\n	}\n\n	function pref ($key) {\n		return @$this->parent->pref($key);\n	}\n\n	function redirect ($vars = '''') {\n		$this->parent->redirect($vars);\n	}\n\n	function set_preference ($key, $value, $type = '''') {\n		return $this->parent->set_preference($key, $value, $type);\n	}\n\n	function remove_preference ($key) {\n		return $this->parent->remove_preference($key);\n	}\n\n	function url ($vars, $gp = false) {\n		return $this->parent->url($vars, $gp);\n	}\n\n	function form_inputs () {\n		return $this->parent->form_inputs();\n	}\n}\n\nclass GBPPreferenceTabView extends GBPAdminTabView {\n\n	var $permissions = ''prefs'';\n\n	function GBPPreferenceTabView (&$parent, $is_default = NULL) {\n		// Call the parent constructor\n		GBPAdminTabView::GBPAdminTabView(gTxt(''tab_preferences''), ''preference'', $parent, $is_default);\n	}\n\n	function preload () {\n		if (ps(''step'') == ''prefs_save'') {\n			foreach ($this->parent->preferences as $key => $pref) {\n				extract($pref);\n				$value = ps($key);\n				if (is_callable(array(&$this->parent, $type)))\n					$value = call_user_func(array(&$this->parent, $type), ''ui_out'', $value);\n				$this->parent->set_preference($key, $value);\n			}\n		}\n	}\n\n	function main () {\n		// Make txp_prefs.php happy :)\n		global $event;\n		$event = $this->parent->event;\n\n		include_once txpath.''/include/txp_prefs.php'';\n\n		echo\n			''<form action="index.php" method="post">'',\n			startTable(''list'');\n\n		foreach ($this->parent->preferences as $key => $pref) {\n			extract($pref);\n\n			$out = tda(gTxt($key), '' style="text-align:right;vertical-align:middle"'');\n\n			switch ($type) {\n				case ''text_input'':\n					$out .= td(pref_func(''text_input'', $key, $value, 20));\n				break;\n				default:\n					if (is_callable(array(&$this->parent, $type)))\n						$out .= td(call_user_func(array(&$this->parent, $type), ''ui_in'', $value, $key));\n					else\n						$out .= td(pref_func($type, $key, $value, 50));\n				break;\n			}\n\n			$out .= tda($this->popHelp($key), '' style="vertical-align:middle"'');\n			echo tr($out);\n		}\n\n		echo\n			tr(tda(fInput(''submit'', ''Submit'', gTxt(''save_button''), ''publish''), '' colspan="3" class="noline"'')),\n			endTable(),\n			$this->parent->form_inputs(),\n			sInput(''prefs_save''),\n			''</form>'';\n	}\n\n	function popHelp ($helpvar) {\n		$script = hu.basename(txpath).''/index.php'';\n		return ''<a href="''.$script.''?event=plugin&step=plugin_help&name=''.$this->parent->plugin_name.''#''.$helpvar.''" class="pophelp">?</a>'';\n	}\n}\n\nclass GBPWizardTabView extends GBPAdminTabView {\n\n	var $installation_steps = array();\n	var $wiz_report = array();\n	var $permissions = ''admin.edit'';\n\n	function GBPWizardTabView (&$parent, $is_default = NULL, $title = ''Wizards'') {\n		global $textarray;\n\n		#\n		#	Get the strings and merge into the textarray before we get the steps...\n		#\n		$strings = $this->get_strings();\n		$textarray = array_merge($strings , $textarray);\n\n		#\n		#	Now get the steps...\n		#\n		$this->installation_steps = $this->get_steps();\n\n		// Call the parent constructor\n		GBPAdminTabView::GBPAdminTabView($title, ''wizard'', $parent, $is_default);\n	}\n\n	function get_steps () {\n		#\n		#	Override this method in derived classes to return the appropriate setup/cleanup steps.\n		#\n		$steps = array(\n			''basic'' 		=> array(''setup'' => ''Basic setup step'', ''cleanup'' => ''Basic cleanup step''),\n			''optional''		=> array(''setup'' => ''Optional setup step'', ''cleanup'' => ''Optional cleanup step'', ''optional'' => true , ''checked'' => 0),\n			''has_options''	=> array(''setup'' => ''Setup step with a option'', ''cleanup'' => ''Cleanup step with a option'', ''has_options'' => true),\n		);\n		return $steps;\n	}\n\n	function get_strings ($language = '''') {\n		#\n		#	Override this function in derived classes to define/change the set of strings to\n		# inject into $textarray to localise the wizard.\n		#\n		$strings = array(\n			''gbp_adlib_wiz-version_errors''		=> ''Version Errors'',\n			''gbp_adlib_wiz-version_reason''		=> ''This plugin cannot operate in this installation because&#8230;'',\n			''gbp_adlib_wiz-version_item''		=> ''It requires <strong class="failure">{name} {min}</strong> or above, current install is {current}.'',\n			''gbp_adlib_wiz-setup'' 				=> ''Setup'',\n			''gbp_adlib_wiz-setup_steps'' 		=> ''The following setup steps will be taken&#8230;'',\n			''gbp_adlib_wiz-setup_report''		=> ''Setup Report&#8230;'',\n			''gbp_adlib_wiz-cleanup'' 			=> ''Cleanup'',\n			''gbp_adlib_wiz-cleanup_steps'' 		=> ''The following cleanup steps will be taken&#8230;'',\n			''gbp_adlib_wiz-cleanup_report''		=> ''Cleanup Report&#8230;'',\n			''gbp_adlib_wiz-cleanup_next'' 		=> ''The plugin can now be disabled and/or uninstalled.'',\n			''gbp_adlib_wiz-done'' 				=> ''Done'',\n			''gbp_adlib_wiz-skipped''				=> ''Skipped'',\n			''gbp_adlib_wiz-failed''				=> ''Failure'',\n			''gbp_adlib_wiz-step_basic''			=> ''Basic Step'',\n			''gbp_adlib_wiz-step_optional''		=> ''Optional step'',\n			''gbp_adlib_wiz-step_complex''		=> ''Step with option(s)'',\n			''gbp_adlib_wiz-step_complex_txt''	=> ''This {step} step has an option/options.'',\n		);\n		return $strings;\n	}\n\n	function versions_ok () {\n		$msg = '''';\n\n		#\n		#	Check the plugin can run in this environment.\n		#\n		#	Return: TRUE -> Yes.\n		#	HTML formatted string -> No, and explain why.\n		#\n		$tests = $this->get_required_versions();\n		if (count($tests))\n			foreach ($tests as $name => $versions) {\n				if( array_key_exists( ''custom_handler'' , $versions ) && is_callable( $versions[''custom_handler''] ) ) {\n					#\n					# Allow derived classes to define their own checking routines...\n					#\n					$fn = $versions[''custom_handler''];\n					$res = call_user_func( $fn , $name , $versions );\n					if( $res )\n						$msg[] = tag($res , ''li'', '' style="text-align: left; padding-top: 0.75em;"'');\n				}\n				else {\n					if (version_compare($versions[''current''], $versions[''min''] , ''<'')) {\n						$res = gTxt(''gbp_adlib_wiz-version_item'' , array(''{name}'' => $name , ''{min}'' => $versions[''min''] , ''{current}'' => $versions[''current'']));\n						$msg[] = tag($res , ''li'', '' style="text-align: left; padding-top: 0.75em;"'');\n					}\n				}\n			}\n\n		if (!empty($msg))\n			return tag(join('''' , $msg) , ''ol'');\n\n		return true;\n	}\n\n	function get_required_versions () {\n		global $prefs;\n\n		#\n		#	Override this function to return an array of tests to be carried out.\n		#\n		$tests = array(''TxP'' => array(\n			''current''	=> $prefs[''version''],\n			''min''		=> ''4.0.3'',\n		));\n		return $tests;\n	}\n\n	function main () {\n		$out[] = ''<style type="text/css"> .success { color: #009900; } .failure { color: #FF0000; } .skipped { color: #0000FF; } </style>'';\n		$out[] = ''<div style="border: 1px solid gray; width: 50em; text-align: center; margin: 1em auto; padding: 1em; clear: both;">'';\n\n		$feaildset_style = '' style="text-align: left; padding: 1em 0"'';\n\n		$step = gps(''step'');\n		if (empty($step)) {\n			$result = $this->versions_ok();\n			if (is_string($result))\n				$step = ''version_error'';\n			else\n				$step = ($this->installed()) ? ''cleanup-verify'' : ''setup-verify'';\n\n			$_POST[''step''] = $step;\n		}\n\n		switch ($step) {\n			case ''version_error'':\n				$out[] = hed(gTxt(''gbp_adlib_wiz-version_errors'') , 1);\n				$out[] = graf(gTxt(''gbp_adlib_wiz-version_reason''));\n				$out[] = $result;\n			break;\n\n			case ''setup-verify'':\n			// Render the setup wizard initial step...\n				$out[] = hed(gTxt(''gbp_adlib_wiz-setup'') , 1);\n				$out[] = graf(gTxt(''gbp_adlib_wiz-setup_steps''));\n				$out[] = tag(tag($this->wizard_steps(''setup'') , ''ol'') , ''fieldset'', $feaildset_style);\n				$out[] = fInput(''submit'', '''', gTxt(''gbp_adlib_wiz-setup''), '''');\n				$out[] = $this->form_inputs();\n				$out[] = sInput(''setup'');\n			break;\n\n			case ''setup'':\n			// Render the post-setup screen...\n				$out[] = hed(gTxt(''gbp_adlib_wiz-setup_report'') , 1);\n				$out[] = tag($this->wizard_report() , ''fieldset'', $feaildset_style);\n				$out[] = fInput(''submit'', '''' , gTxt(''next'') , '''');\n				$out[] = eInput($this->parent->event);\n				$out[] = hInput(gbp_tab, ''preference'');\n			break;\n\n			case ''cleanup-verify'':\n			// Render the cleanup wizard initial step...\n				$out[] = hed(gTxt(''gbp_adlib_wiz-cleanup'') , 1);\n				$out[] = graf(gTxt(''gbp_adlib_wiz-cleanup_steps''));\n				$out[] = tag(tag($this->wizard_steps(''cleanup'') , ''ol'') , ''fieldset'', $feaildset_style);\n				$out[] = fInput(''submit'', '''', gTxt(''gbp_adlib_wiz-cleanup''), '''');\n				$out[] = $this->form_inputs();\n				$out[] = sInput(''cleanup'');\n			break;\n\n			case ''cleanup'':\n			// Render the post-cleanup screen...\n				$out[] = hed(gTxt(''gbp_adlib_wiz-cleanup_report'') , 1);\n				$out[] = tag($this->wizard_report() , ''fieldset'', $feaildset_style);\n				$out[] = graf(gTxt(''gbp_adlib_wiz-cleanup_next''));\n				$out[] = fInput(''submit'', '''' , gTxt(''next'') , '''');\n				$out[] = eInput(''plugin'');\n			break;\n		}\n\n		$out[] = ''</div>'';\n\n		$verify = (in_array($step, array(''setup-verify'', ''cleanup-verify'')))\n			? "verify(''".doSlash(gTxt(''are_you_sure''))."'')"\n			: '''';\n\n		echo form(join(n, $out), '''', $verify);\n	}\n\n	function installed () {\n		return false;\n	}\n\n	function wizard_steps ($step) {\n		$step_details = '''';\n\n		foreach ($this->installation_steps as $key => $detail) {\n			if (@$detail[$step]) {\n				$options = '''';\n				if (@$detail[''has_options'']) {\n					$function = array(&$this, ''option_''.$key);\n					if (is_callable($function))\n						$options = n.tag(call_user_func($function, $step), ''span'', '' id="wizard_''.$key.''" style="display: block; margin-right: 1em; padding: 0.5em; background-color: #eee;"'');\n				}\n\n				$checkbox = '''';\n				if (@$detail[''optional'']) {\n					$checked = (isset($detail[''checked''])) ? $detail[''checked''] : 0 ;\n					$checkbox = checkbox2(''optional_''.$key, $checked, ($options ? ''" onclick="toggleDisplay(\\''wizard_''.$key.''\\'');'' : ''''));\n				}\n\n				$step_details .= n.tag(graf(\n					tag($detail[$step].$checkbox, ''label'').$options\n				), ''li'');\n			}\n		}\n\n		return $step_details.n;\n	}\n\n	function wizard_report () {\n		// Render the wizard report as an ordered list. There maybe\n		// ''sub'' reports which we need to also render as ordered lists\n		$out = array();\n		foreach ($this->wiz_report as $report) {\n			$out_sub = array();\n\n			// Skip the first element as it is in fact the parent report\n			next($report);\n\n			// Lets generate a sub report - if there are more elements\n			while (list($key, $report_sub) = each($report))\n				$out_sub[] = tag($report_sub , ''li'');\n\n			// Check to see if we actually have a sub report - tag it as necessary\n			$out_sub = (count($out_sub) > 0)\n				? tag(join(n , $out_sub), ''ol'')\n				: '''';\n\n			$out[] = tag($report[0] . $out_sub , ''li'');\n		}\n		return tag(join(n , $out) , ''ol'');\n	}\n\n	function add_report_item ($string , $ok = NULL, $sub = false) {\n		if (isset($ok)) {\n			switch ($ok) {\n				case ''1'' :\n					$class = ''success'';\n					$okfail = gTxt(''gbp_adlib_wiz-done'');\n				break;\n\n				default :\n				case ''0'' :\n					$class = ''failure'';\n					$okfail = gTxt(''gbp_adlib_wiz-failed'');\n				break;\n\n				case ''skipped'' :\n					$class = ''skipped'';\n					$okfail = gTxt(''gbp_adlib_wiz-skipped'');\n				break;\n			}\n			$okfail = '' : <span class="''.$class.''">''.tag($okfail, ''strong'').''</span>'';\n		}\n\n		$line = graf($string . (isset($okfail) ? $okfail : ''''));\n\n		if ($sub && count($this->wiz_report) > 0)\n			$this->wiz_report[count($this->wiz_report) - 1][] = $line;\n		else\n			$this->wiz_report[] = array($line);\n	}\n\n	function setup_basic () {\n		$this->add_report_item(gTxt(''gbp_adlib_wiz-step_basic''), true);\n	}\n\n	function cleanup_basic () {\n		$this->add_report_item(gTxt(''gbp_adlib_wiz-step_basic''), false);\n	}\n\n	function setup_optional () {\n		$this->add_report_item(gTxt(''gbp_adlib_wiz-step_optional''), true);\n	}\n\n	function cleanup_optional () {\n		$this->add_report_item(gTxt(''gbp_adlib_wiz-step_optional''), false);\n	}\n\n	function setup_has_options () {\n		$this->add_report_item(gTxt(''gbp_adlib_wiz-step_complex''), true);\n	}\n\n	function cleanup_has_options () {\n		$this->add_report_item(gTxt(''gbp_adlib_wiz-step_complex''), false);\n	}\n\n	function option_has_options ($step) {\n		return graf(gTxt(''gbp_adlib_wiz-step_complex_txt'' , array(''{step}'' => $step))).yesnoRadio(''wizard_has_options_test'', 1);\n	}\n}\n', 'cdf7edc000676938a422ef4becfbf195', 2, 5, 0);
INSERT INTO `txp_plugin` (`name`, `status`, `author`, `author_uri`, `version`, `description`, `help`, `code`, `code_restore`, `code_md5`, `type`, `load_order`, `flags`) VALUES
('rvm_maintenance', 0, 'Ruud van Melick', 'http://vanmelick.com/', '0.5', 'Maintenance Mode', '	<h1>Maintenance Mode</h1>\n\n	<p>This plugin, when activated, will show a maintenance page to all visitors who are not logged in on the admin side of Textpattern.</p>\n\n	<p>After enabling the plugin, click the <em>View Site</em> tab. This will allow you to see what your website will look like to visitors once the maintenance mode is deactivated.</p>\n\n	<p>There are two ways to customize the maintenance mode error page<sup class="footnote"><a href="#fn4788520324d470396d5efc">1</a></sup>:\n	<ol>\n		<li>modify the <em>error_default</em> template page.</li>\n		<li>create a new template page called <em>error_503</em> (recommended!)</li>\n	</ol></p>\n\n	<p>To disable maintenance mode, simply deactivate the plugin. This will make your entire site viewable to all visitors.</p>\n\n	<p>This plugin requires Textpattern 4.0.7 or higher.</p>\n\n	<p id="fn4788520324d470396d5efc" class="footnote"><sup>1</sup> Maintenance mode uses a <em>503 Service Unavailable</em> <span class="caps">HTTP</span> status header.</p>', 'register_callback(''rvm_maintenance_init'', ''pretext'');\r\n\r\nfunction rvm_maintenance_init()\r\n{\r\n  if (txpinterface == ''public'' and !gps(''txpcleantest'') and !is_logged_in())\r\n  {\r\n    $_GET = $_POST = $_REQUEST = array();\r\n    register_callback(''rvm_maintenance'', ''pretext_end'');\r\n  }\r\n}\r\n\r\nfunction rvm_maintenance()\r\n{\r\n    txp_die(''Site maintenance in progress. Please check back later.'', 503);\r\n}\r\n', '\nregister_callback(''rvm_maintenance_init'', ''pretext'');\n\nfunction rvm_maintenance_init()\n{\n  if (txpinterface == ''public'' and !gps(''txpcleantest'') and !is_logged_in())\n  {\n    $_GET = $_POST = $_REQUEST = array();\n    register_callback(''rvm_maintenance'', ''pretext_end'');\n  }\n}\n\nfunction rvm_maintenance()\n{\n    txp_die(''Site maintenance in progress. Please check back later.'', 503);\n}\n', '543f28e4542434c60c0b680ae6764a3b', 0, 5, 0);
INSERT INTO `txp_plugin` (`name`, `status`, `author`, `author_uri`, `version`, `description`, `help`, `code`, `code_restore`, `code_md5`, `type`, `load_order`, `flags`) VALUES
('ebl-image-edit', 1, 'Eric Limegrover', 'http://www.syserror.net/', '2.0', 'Advanced Image Editing Plugin for Textpattern', '<style type="text/css" >h1, h2, h3, p {text-align:left}h1{font-size:28px; letter-spacing:.01em; word-spacing:.1em; color:#444}h2{font-size:14px; margin:5px 0 15px; color:#AAA; border-bottom:1px solid #DDD; padding:0 0 5px; letter-spacing:.05em; word-spacing:.1em}h3{font-size:16px; margin:10px 0; color:#444; font-weight: 900;}p{margin:15px 0; font-size:14px; line-height:24px}.dropcap{float:left; color:#B4B4B7; font-size:50px; line-height:40px; padding-top:2px; margin:0 3px 0 0; font-family:Times,serif,Georgia}.caps{text-transform:uppercase; letter-spacing:.15em; font-size:12px; color:#555}#main {font-family:Georgia, "Times New Roman", Times, serif; font-size:14px;font-weight:400;}	</style>\r\n\r\n<div id="main">\r\n<h1>EBL Image Edit</h1>\r\n <h2>Advanced Image Editing Plugin for Textpattern</h2>\r\n <p><span class="dropcap">T</span>his plugin introduces advanced image editing functionality to Textpattern 4.07 / 4.08. \r\n Users may now custom crop, resize, rotate, and create custom thumbnails directly within Textpattern.</p>\r\n\r\n <h3>Features</h3>\r\n <p>This plugin allows for functions previously only available in image-editing programins. It allows for users, at any terminal which they''re logged in to to do the following: \r\n <ul>\r\n 	<li><strong>Cropping</strong>: The original uploaded image can be edited to cut out parts that are not desired and keep only those important facets of the original image.</li>\r\n 	<li><strong>Thumbnail Crop</strong>: Make a thumbnail show not the entire image, but just enough to pique interest.</li>\r\n 	<li><strong>Rotate</strong>: For those pictures that are sideways or upside down. Users may rotate the image clockwise90&deg;, counterclockwise90&deg; or rotate a full 18090&deg;.</li>\r\n 	<li><strong>Resize</strong>: Many digital images are just too large to be shown on the web. This option allows for excessively sized images to be scaled down. Built into the resizing function is the ability to maintain the original height to width ratio, so images will never seem squished one way or the other.</li>\r\n 	<li><strong>Image Backups</strong>: New for <i>2.0</i> is the feature to save an original copy of the the image to a temp folder so that you may restore it at any time. The image must first be saved by clicking on backup, and then may be restored at any time.</li>\r\n </ul></p>\r\n <h3>Installation</h3>\r\n <p>Installation is straight forward and involves the installation of one plugin file, and the upload of one directory to your Textpattern folder.</p>\r\n <p>Contained within the zip archive are two files:\r\n  <ol>\r\n   <li><b>ebl-image-edit_v1.0.txt</b> : This is the plugin code that is pasted into the textbox in <i>Admin -> Plugins</i></li>\r\n   <li><b>jcrop</b> : This folder will need to be uploaded to the Textpattern directory (e.g., /textpattern/ )</li>\r\n  </ol>\r\n </p>\r\n <p>Once the above has been completed, activate the plugin in <i>Admin -> Plugins</i></p>\r\n <p>Once the plugin has been activated, navigate to <i>Content -> Extensions -> EBL Image Edit</i>. The temp folder and db field will be created. The plugin will be ready for use at this point.</p> \r\n  \r\n <h3>Usage Instructions</h3>\r\n <p>Further help can be found online in the textpattern forum topic: "<strong><a href="http://forum.textpattern.com/viewtopic.php?id=29547">ebl image edit v1.0 </a></strong>"</p>\r\n <ul>\r\n 	<li><b>Crop</b>: This tab permits the cropping of images. Click and drag across the image to select the crop area. While selecting the crop region, the size of the crop area willbe displayed in as Width X Height. This is measured in pixels. You''ll note that the area outside of the crop region is dark, whereas the area inside is lighter. This is the intended effect. Everything within the crop area will be selected and will replace the original image once the <b>Crop Original</b> button is clicked.\r\n	<ul>\r\n		<li><i>Predefined size</i>: This drop-down box allows the user to select pre-defined crop area selections. They may be moved around anywhere on the image. If needed, these areas can be sized differently, but the aspect ratio of the original will remain. <i>(Found in Thumbnail Crop as well.)</i><li>\r\n		<li><i>Lock Aspect Ratio</i>: Unchecking this box will allow for a different aspect ratio than what was selected in the drop down box. <i>(Found in Thumbnail Crop as well.)</i></li>\r\n	</ul></li>\r\n 	<li><b>Thumbnail Crop</b>: Nearly identical in function to the main image crop, the thumbnail crop allows for a selected region to be highlighted to act as the primary image thumbnail. To create the cropped thumbnail, select the crop-area. If you want the image resized to a specific dimension, check <i>Resize Thumbnail</i> and edit the size accordingly. Once you are satisified with the crop area and the options, click on the Create Thumbnail button. </li>\r\n 	<li><b>Rotate</b>: This tab allows three seperate methods of rotating. 1. Rotate Clockwise 90&deg; (Turn Right), 2. Counter-Clockwise 90&deg; (Left Turn) or 3. 180&deg; (Flip). Clicking any of these buttons will rotate the original button. <em>Do not repeatedly rotate the images as this will gradually degrade image quality</em>.</li>\r\n 	<li><b>Resize</b>: This tab allows the for a longest-side resize of an original image. Simply input the desired dimensions and the image will be sized down to fit. Note, if the image is smaller in any one dimension that which was entered, it will not be sized up to fit, but rather, sized down in proportion with the longest side. If the image is entirely smaller than the dimensions entered, it will not be resized. </li>\r\n	<li><b>Backup</b>: This tab allows for the backing-up of an image into a backup folder located within your image directory. When active, two buttons are displayed. The first, Backup will immediately copy the associated image to the backup directory and overwrite any identically named file. Clicking Restore will copy that image back to the main image directory but will still maintain the copied file in this location.</li>\r\n </ul></p>\r\n</div>\r\n\r\n</div>', 'if (@txpinterface == ''admin'')\r\n	{\r\n		add_privs(''eblimgeditext'', ''1''); \r\n		register_tab("extensions", "eblimgeditext", "EBL Image Edit");\r\n		register_callback("ebl_image_edit_ext", "eblimgeditext");\r\n\r\n	switch (gps(''event''))\r\n	{\r\n		case ''eblrotateleft'':\r\n			ebl_img_edit(gps(''imgid''), ''left'', ''rotate''); exit();\r\n			break;\r\n		case ''eblrotateright'':\r\n			ebl_img_edit(gps(''imgid''), ''right'', ''rotate''); exit();\r\n			break;\r\n		case ''eblrotateup'':\r\n			ebl_img_edit(gps(''imgid''), ''up'', ''rotate''); exit();\r\n			break;\r\n		case ''eblimgcrop'':\r\n			ebl_img_edit(gps(''imgid''), '''', ''crop''); exit();\r\n			break;\r\n		case ''eblimgcrop'':\r\n			ebl_processUpload(gps(''uploadType''));\r\n			break;\r\n		case ''ebltmbcrop'':\r\n			ebl_img_edit(gps(''imgid''), '''', ''thumbnail''); exit();\r\n			break;\r\n		case ''eblrszimg'':\r\n			ebl_img_edit(gps(''imgid''), '''', ''rsz''); exit();\r\n			break;\r\n		case ''eblbackupimg'':\r\n			eblbackupimg(gps(''imgid''));\r\n			break;\r\n		case ''eblrestoreimg'':\r\n			eblrestoreimg(gps(''imgid''));\r\n			break;\r\n	}\r\n}\r\n\r\nfunction ebl_image_edit_ext() \r\n{\r\n	\r\n	$step = gps(''step'');\r\n	\r\n	$message = (is_callable($step)) ? $step() : '''';\r\n	\r\n	echo pagetop("EBL Image Edit Preferences", $message);\r\n	\r\n	echo 	''<div style="margin: 0 auto; width: 400px;">''.n.\r\n			ebl_list_cropStyles().\r\n			''</div>'';	\r\n}\r\n\r\nfunction ebl_list_cropStyles()\r\n{\r\n	global $txp_user, $path_to_site, $img_dir;\r\n	\r\n	$backupdir 	= $path_to_site . ''/'' . $img_dir . ''/backup/'';\r\n		if(!file_exists($backupdir)) {\r\n			\r\n		$createEBLtable = safe_query("CREATE TABLE `".PFX."ebl_crop` (".\r\n									"`name` varchar(64) NOT NULL,".\r\n									"`width` varchar(16) NOT NULL,".\r\n									"`height` varchar(16) NOT NULL,".\r\n									"`thumb` varchar(1) NOT NULL default ''0'',".\r\n									"UNIQUE KEY `name` (`name`)".\r\n									");");\r\n									\r\n		$rs = safe_insert(''ebl_crop'', "`name` = ''Image Crop'', `width` = ''500'',`height` = ''500'', `thumb` = ''0''");\r\n		$rs = safe_insert(''ebl_crop'', "`name` = ''Thumbnail Square'', `width` = ''100'',`height` = ''100'', `thumb` = ''1''");\r\n\r\n			$mkdir = (@mkdir($backupdir)) ? TRUE : FALSE;\r\n			if($mkdir) { \r\n				echo "<p  style=\\"text-align: center; \\">$backupdir did not exist previously. Now created.</p>";\r\n			} else {\r\n				echo "<p style=\\"text-align: center; font-weight: bold; \\">Unable to create $backupdir. Please check your folder permissions.</p>";\r\n			}\r\n		}\r\n\r\n\r\n \r\n	echo n.n.hed(gTxt(''Crop Styles''), 1, '' style="text-align: center; margin-top:2em; font-weight: bold;"'').\r\n		n.n.startTable(''list'').\r\n		n.tr(\r\n			n.hCell(gTxt(''name'')).\r\n			n.hCell(gTxt(''width'')).\r\n			n.hCell().\r\n			n.hCell(gTxt(''height'')).\r\n			n.hCell(gTxt(''thumbnail'')).\r\n			n.hCell(gTxt(''delete''))\r\n		);\r\n\r\n	$rs = safe_rows_start(''*'', ''ebl_crop'', ''1=1 ORDER BY `name`'');\r\n\r\n	$out = '''';\r\n	\r\n	if ($rs)\r\n	{ \r\n		while ($a = nextRow($rs))\r\n		{ \r\n			$isChecked = ($a[''thumb''] == 1) ? ''yes'' : ''no'';\r\n			$out .= n.tr(\r\n					td( htmlspecialchars($a[''name''])).\r\n					td( htmlspecialchars($a[''width''])).\r\n					td( htmlspecialchars('' X '')).\r\n					td( htmlspecialchars($a[''height''])).\r\n					td( $isChecked).\r\n					td( \r\n						dLink(''eblimgeditext'', ''ebl_cropdelete'', ''stylename'', $a[''name''])\r\n					)\r\n				);\r\n		}\r\n	}\r\n	\r\n	echo form($out);\r\n	\r\n	echo n.tr(\r\n		form(\r\n			td( fInput(''text'', ''name'', '''', ''edit'','''','''',10) ).\r\n			td( fInput(''text'', ''width'', '''', ''edit'','''','''',5) ).\r\n			td().\r\n			td( fInput(''text'', ''height'', '''', ''edit'','''','''',5) ).\r\n			td( ''<input name="eblistmb" class="checkbox" type="checkbox">'').\r\n			td( fInput(''submit'', ''add'', gTxt(''add''), ''smallerbox'') ).\r\n			n.eInput(''eblimgeditext'').\r\n			n.sInput(''ebl_cropsavenew'')\r\n		)\r\n	);\r\n\r\n	echo n.endTable();\r\n}\r\n\r\nfunction selectStyles($thumb = ''0'') {\r\n\r\n	$rs = safe_rows_start(''*'', ''ebl_crop'', ''thumb=''.$thumb.'' ORDER BY `name`'');\r\n\r\n	$out = "<select name=\\"customsize\\" id=\\"customsize$thumb\\">'' + ".n.\r\n		   "''<option value=\\"Custom\\">Custom</option>'' + ".n;\r\n	\r\n	if ($rs)\r\n	{ \r\n		while ($a = nextRow($rs))\r\n		{\r\n			$width	= $a[''width''];\r\n			$height	= $a[''height''];\r\n			$name 	= $a[''name''];\r\n\r\n			$out .= "''<option value=".$width."x".$height."\\>".htmlspecialchars($name)." : $width x $height </option>'' + ".n;\r\n		}\r\n	}\r\n	$out .= "''</select>";\r\n	\r\n	return $out;\r\n}\r\nfunction ebl_cropsavenew()\r\n{\r\n\r\n	extract(doSlash(psa(array(''name'', ''width'',''height'',''eblistmb''))));\r\n	\r\n	if ($name && is_numeric($width) && is_numeric($height))\r\n	{ \r\n		$eblistmb = ($eblistmb == "on") ? 1 : 0;\r\n		\r\n		$rs = @safe_insert(''ebl_crop'', "\r\n			`name`	 = ''$name'',\r\n			`width`	 = ''$width'',\r\n			`height` = ''$height'',\r\n			`thumb`  = ''$eblistmb''");\r\n\r\n		return ($rs) ?\r\n			"New crop style created" : \r\n			"<b>Error:</b> Duplicate Name";	\r\n		\r\n	} else {\r\n		\r\n		return "<b>Width</b> and <b>Height</b> must be numeric values";\r\n	}\r\n	\r\n	return FALSE;\r\n}\r\n\r\nfunction ebl_cropdelete()\r\n{\r\n	$name = ps(''stylename'');\r\n	return (safe_delete(''ebl_crop'', "`name` = ''".doSlash($name)."''")) ? \r\n		"<b>Deleted</b> $name" : \r\n		"<b>Error</b> Unable to delete $name"; \r\n}\r\n\r\n\r\nif(gps(''step'') == ''image_edit'' || gps(''step'') == ''thumbnail_insert'' || gps(''step'') == ''image_replace'' || gps(''step'') == ''thumbnail_create'' || gps(''step'') == ''thumbnail_delete'') {\r\n	register_callback(''ebl_imgcrop'', ''admin_side'', ''head_end'');\r\n}\r\n\r\nfunction ebl_imgcrop() {\r\n\r\n	global $path_to_site,$img_dir;\r\n\r\n	$id=gps(''id'');\r\n	$imagedir = hu . $img_dir . ''/'';\r\n\r\n	$rs = @safe_row("*, unix_timestamp(date) as uDate", "txp_image", "id = $id");\r\n\r\n	if ($rs) {\r\n		extract($rs);\r\n		$filename = $id.$ext;\r\n		$imgsrc = $imagedir.$filename;\r\n		$tmbsrc = $imagedir.$id.''t''.$ext;\r\n	}\r\n	\r\n	$customThumbSize = selectStyles(1);\r\n	$customImgSize = selectStyles(0);\r\n	\r\n	echo <<< EOF\r\n<script src="jcrop/jquery.Jcrop.pack.js"></script>\r\n<link href="jcrop/jquery.Jcrop.css" rel="stylesheet" type="text/css" />\r\n<style type="text/css">\r\n#eblimgprocess {\r\n	margin: 5px;\r\n	font-weight: bold; \r\n	font-size: 14pt;\r\n	color: #550000;\r\n	padding: 0 0 0 20px;\r\n	background: url(''jcrop/processing.gif'') left no-repeat;\r\n}\r\na { padding: 0 0 3px;}\r\na:visited {} a:hover {} a:active { border: 0; }\r\na:focus { outline: 0; text-decoration: none;} \r\n</style>\r\n<script type="text/javascript">\r\n$(document).ready(function() { // init everything\r\n\r\n	$("#list td:first img").attr(''id'',''mainImg''); // Give our image an ID\r\n	$("#list td:first").attr(''id'',''mainImgTD''); // name the table to make it easier to find.\r\n\r\n	$(''#image-thumbnail td:first img'').attr(''id'',''thumb''); // Give our thumbnail image an ID\r\n	$(''#image-thumbnail td:first'').attr(''id'',''thumbTD''); // name the TD that contains the image so we can swap it out.\r\n\r\n	if(! $(''#thumbTD'').length ) {  $("#list td:eq(1)").attr(''id'',''thumbTD'');  }\r\n\r\n	$(''#mainImgTD'').prepend(''<div id="eblcropui"></div>'');\r\n	$(''#eblcropui'').append(''<fieldset style="min-width: 500px;"><legend>Edit Image</legend><div id="eblcontainer"></div></fieldset>'');\r\n	\r\n	$(''#eblcontainer'').css({''margin'':''10px''});\r\n	$(''#eblcontainer'').append(\r\n						''<p>'' + \r\n						 ''<a href="#" id="eblcroplnk" >Crop</a> | '' + \r\n						 ''<a href="#" id="ebltmblnk" >Thumbnail</a> | '' + \r\n						 ''<a href="#" id="eblrotatelnk" >Rotate</a> | '' +\r\n						 ''<a href="#" id="eblresizelnk" >Resize</a> | '' + \r\n						 ''<a href="#" id="eblbackuplnk" >Backup</a>'' + \r\n						''</p>''\r\n						);\r\n	\r\n	$(''#eblcontainer'').append(''<div id="eblcropdata"></div><div id="eblcropctrl"></div><div id="ebltmbctrl"></div><div id="eblrotatectrl"></div><div id="eblresize"></div><div id="eblbackupimg"></div><div id="eblhiddendata"></div><div id="eblimgprocess"> Processing...</div>'');\r\n	\r\n	$(''#eblcropctrl'').append(\r\n								 ''<input type="hidden" id="eblcropHh" name="eblcropH" value="" />'' +\r\n								 ''<input type="hidden" id="eblcropWh" name="eblcropW" value="" />'' +\r\n								 ''<input type="hidden" id="eblcropXh" name="eblcropXh" value="" />'' +\r\n								 ''<input type="hidden" id="eblcropYh" name="eblcropYh" value="" />''\r\n							);\r\n	\r\n	$(''#eblcropctrl'').append(\r\n								''<p>Use predefined size: $customImgSize | <input name="aspectratio" type="checkbox" id="imgaspectratio" value="" /> Lock Aspect Ratio </p>'' +\r\n								''<input type="submit" name="$id" id="eblimgcrop" value="Crop Original" class="smallerbox" />''\r\n							);\r\n	\r\n	$(''#ebltmbctrl'').append(					     	\r\n								''</p><p>Use predefined size: $customThumbSize | '' + \r\n								''<input name="aspectratio" type="checkbox" id="tmbaspectratio" value="" /> Lock Aspect Ratio </p></p><p> Thumbnail Dimensions: '' + \r\n								 ''( W: <input name="ebltmbW" type="text" id="ebltmbW" size="5" maxlength="4" value="100" /> ) x '' +\r\n								 ''( H: <input name="ebltmbH" type="text" id="ebltmbH" size="5" maxlength="4" value="100" /> )'' + \r\n								 ''| <input name="cropthumb" type="checkbox" id="cropthumb" checked="true"/> Resize Thumbnail '' + \r\n								''</p><p>'' +  \r\n								 ''<input type="button" name="$id" id="ebltmbcrop" value="Create Thumbnail" class="smallerbox" />'' + \r\n								''</p>''\r\n							);\r\n	\r\n	$(''#eblrotatectrl'').append(\r\n								''<p>Rotate <input type="button" name="$id" id="rotateright" value="Clockwise 90&deg;" class="smallerbox" /> '' +\r\n								 ''<input type="button" name="$id" id="rotateleft" value="Counterclockwise 90&deg; " class="smallerbox" /> '' +\r\n								 ''<input type="button" name="$id" id="rotateup" value="180 &deg; " class="smallerbox" />'' + \r\n								''</p>''\r\n							);\r\n							\r\n	$(''#eblbackupimg'').append(\r\n								''<p><input type="button" name="$id" id="eblbackup" value="Backup" class="smallerbox" /> '' +\r\n								''<input type="button" name="$id" id="eblrestore" value="Restore" class="smallerbox" />'' + \r\n								''</p>''\r\n							);\r\n	\r\n	$(''#eblresize'').append(\r\n								''<p>Current Image Size : W[ <span id="origW"></span> ] x H[ <span id="origH"></span> ]</p>'' + \r\n								''<p><label for="eblrszW">Width: </label><input type="text" name="eblrszW" id="eblrszW" /> x '' +\r\n								 ''<label for="eblrszH">Height: </label><input type="text" name="eblrszH" id="eblrszH" /> '' +\r\n							 	 ''<input type="button" name="$id" id="eblrszimg" value="Resize" class="smallerbox" />'' +\r\n								''</p>''\r\n							);\r\n	\r\n	$(''#eblcropdata'').append(\r\n								''<p>Current Crop Area (W: <span style="font-weight: bold;" id="eblcropW">0</span>) x (H: <span style="font-weight: bold;" id="eblcropH">0</span>)</p>''\r\n							);\r\n	\r\n	$(''#eblcropctrl,#eblrotatectrl,#eblresize,#ebltmbctrl,#eblcropdata,#eblimgprocess,#eblbackupimg'').hide();\r\n	\r\n	\r\n	// set our defaults\r\n	window.crop = false;\r\n	window.tmbcrop = false;\r\n\r\n	$(''#mainImg'').wrap(''<div id="imgDiv"></div'');\r\n	$(''#imgaspectratio,#tmbaspectratio'').attr(''disabled'', true).attr(''checked'', false);\r\n\r\n	\r\nfunction removeCrop() {\r\n	// Removes jCrop if found.\r\n	if ( $(''.jcrop-holder'').length ) {\r\n		var x = $(''#imgDiv'');\r\n		\r\n		// generate random number to append to img url to avoid cache