_prefix;\r\n	var $smd_lang;\r\n	var $smd_event;\r\n	function smd_MLP($plug, $prefx, $strarray, $lng=''en-gb'', $ev=''public'') {\r\n		$this->smd_owner = $plug;\r\n		$this->smd_prefix = $prefx;\r\n		$this->smd_strings = $strarray;\r\n		$this->smd_lang = $lng;\r\n		$this->smd_event = $ev;\r\n		register_callback(array(&$this, ''smd_Callback''), ''l10n.enumerate_strings'');\r\n	}\r\n	function smd_Callback($event=''l10n.enumerate_strings'', $step='''', $pre=0) {\r\n		$r = array(\r\n			''owner'' => $this->smd_owner,\r\n			''prefix'' => $this->smd_prefix,\r\n			''lang'' => $this->smd_lang,\r\n			''event'' => $this->smd_event,\r\n			''strings'' => $this->smd_strings,\r\n		);\r\n		return $r;\r\n	}\r\n	// Generic lookup\r\n	//  $what = key to look up\r\n	//  $args = any arguments the key is expecting for replacement\r\n	function gTxt($what, $args = array()) {\r\n		global $textarray;\r\n\r\n		// Prepare the prefixed key for use\r\n		$key = $this->smd_prefix . ''-'' . $what;\r\n		$key = strtolower($key);\r\n\r\n		// Grab from the global textarray (possibly edited by MLP) if we can\r\n		if(isset($textarray[$key])) {\r\n			$str = $textarray[$key];\r\n		} else {\r\n			// The string isn''t in the localised textarray so fallback to using\r\n			// the (non prefixed) string array in the plugin\r\n			$key = strtolower($what);\r\n			$str = (isset($this->smd_strings[$key])) ? $this->smd_strings[$key] : $what;\r\n		}\r\n		// Perform substitutions\r\n		if(!empty($args)) {\r\n			$str = strtr($str, $args);\r\n		}\r\n\r\n		return $str;\r\n	}\r\n}\r\n}\r\n\r\nif (!function_exists("smd_doList")) {\r\nfunction smd_doList($lst, $rng=true, $sub="", $qte=true, $dlm=",", $lax=true) {\r\n	global $thisarticle, $thisimage, $thisfile, $thislink, $pretext, $variable;\r\n\r\n	$inc = $exc = array();\r\n	$lst = do_list($lst, $dlm);\r\n\r\n	// Sometimes pesky Unicode is not compiled in. Detect if so and fall back to ASCII\r\n	if (!@preg_match(''/\\pL/u'', ''a'')) {\r\n		$modRE = ($lax) ? ''/(\\?|\\!)([A-Za-z0-9_\\- ]+)/'' : ''/(\\?|\\!)([A-Za-z0-9_\\-]+)/'';\r\n	} else {\r\n		$modRE = ($lax) ? ''/(\\?|\\!)([\\p{L}\\p{N}\\p{Pc}\\p{Pd}\\p{Zs}]+)/'' : ''/(\\?|\\!)([\\p{L}\\p{N}\\p{Pc}\\p{Pd}]+)/'';\r\n	}\r\n\r\n	foreach ($lst as $item) {\r\n		$mod = 0; // 0 = include, 1 = exclude\r\n		$numMods = preg_match_all($modRE, $item, $mods);\r\n\r\n		for ($modCtr = 0; $modCtr < $numMods; $modCtr++) {\r\n			// mod "type" is governed by the first one found only. i.e. if "article-?c!s" was used in one field\r\n			// it would be an "include" of the word "article-" plus the category and section concatenated\r\n			$mod = ($mods[1][0] === "!") ? 1 : 0;\r\n			$modChar = $mods[1][$modCtr];\r\n			$modItem = trim($mods[2][$modCtr]);\r\n			$lowitem = strtolower($modItem);\r\n			if (isset($variable[$lowitem])) {\r\n				$item = str_replace($modChar.$modItem, $variable[$lowitem], $item);\r\n			} else if (isset($thisimage[$lowitem])) {\r\n				$item = str_replace($modChar.$modItem, $thisimage[$lowitem], $item);\r\n			} else if (isset($thisfile[$lowitem])) {\r\n				$item = str_replace($modChar.$modItem, $thisfile[$lowitem], $item);\r\n			} else if (isset($thislink[$lowitem])) {\r\n				$item = str_replace($modChar.$modItem, $thislink[$lowitem], $item);\r\n			} else if (array_key_exists($lowitem, $pretext)) {\r\n				$item = str_replace($modChar.$modItem, $pretext[$lowitem], $item);\r\n			} else if (isset($_POST[$modItem])) {\r\n				$item = str_replace($modChar.$modItem, $_POST[$modItem], $item);\r\n			} else if (isset($_GET[$modItem])) {\r\n				$item = str_replace($modChar.$modItem, $_GET[$modItem], $item);\r\n			} else if (isset($_SERVER[$modItem])) {\r\n				$item = str_replace($modChar.$modItem, $_SERVER[$modItem], $item);\r\n			} else if (isset($thisarticle[$lowitem])) {\r\n				$item = str_replace($modChar.$modItem, $thisarticle[$lowitem], $item);\r\n			} else {\r\n				$item = str_replace($modChar.$modItem, $modItem, $item);\r\n			}\r\n		}\r\n\r\n		// Handle ranges of values\r\n		$sitem = do_list($item, $dlm);\r\n		foreach ($sitem as $idx => $elem) {\r\n			if ($rng && preg_match(''/^(\\d+)\\-(\\d+)$/'', $elem)) {\r\n				list($lo, $hi) = explode("-", $elem, 2);\r\n				$sitem[$idx] = implode($dlm, range($lo, $hi));\r\n			}\r\n		}\r\n		$item = implode($dlm, $sitem);\r\n\r\n		// Item may be empty; ignore it if so\r\n		if ($item) {\r\n			$item = do_list($item, $dlm);\r\n\r\n			// Handle sub-categories\r\n			if ($sub) {\r\n				list($subtype, $level) = explode(":", $sub);\r\n				$level = (empty($level)) ? 0 : $level;\r\n				$level = (strtolower($level)=="all") ? 99999 : $level;\r\n				$outitems = array();\r\n				foreach ($item as $cat) {\r\n					$cats = getTree(doslash($cat), $subtype);\r\n					foreach ($cats as $jdx => $val) {\r\n						if ($cats[$jdx][''level''] <= $level) {\r\n							$outitems[] = $cats[$jdx][''name''];\r\n						}\r\n					}\r\n				}\r\n				$item = $outitems;\r\n			}\r\n\r\n			// Quote if asked\r\n			$item = ($qte) ? doArray($item, ''doQuote'') : $item;\r\n			if ($mod === 0) {\r\n				$inc = array_unique(array_merge($inc, $item));\r\n			} else {\r\n				$exc = array_unique(array_merge($exc, $item));\r\n			}\r\n		}\r\n	}\r\n	return array($inc, $exc);\r\n}\r\n}\r\n\r\n// Split a string on a pattern and allow integer ranges to be expanded\r\nif (!function_exists("smd_split")) {\r\nfunction smd_split($str, $allowRange=true, $splitat="/(,|,\\s)+/", $pregopt = PREG_SPLIT_NO_EMPTY) {\r\n	$retarr = array();\r\n	if ((substr($splitat,0,1) == "/") && (substr($splitat, strlen($splitat)-1, 1) == "/")) {\r\n		$pat = $splitat;\r\n	} else {\r\n		$pat = ''/[''.$splitat.'']+/'';\r\n	}\r\n	$elems = preg_split($pat, $str, -1, $pregopt);\r\n	foreach ($elems as $item) {\r\n		$item = trim($item);\r\n		$negate = false;\r\n		// Does the item start with a negation character\r\n		if (substr($item,0,1) === "!") {\r\n			$negate = true;\r\n			$item = substr($item,1);\r\n		}\r\n		// Is the item an integer list range\r\n		if ($allowRange && preg_match(''/^(\\d+)\\-(\\d+)$/'', $item)) {\r\n			list($lo, $hi) = explode("-", $item, 2);\r\n			$rng = range($lo, $hi);\r\n			// Reapply the negation if necessary\r\n			for($idx = 0; $idx < count($rng); $idx++) {\r\n				$rng[$idx] = (($negate) ? "!" : "") . $rng[$idx];\r\n			}\r\n			$retarr = array_merge($retarr, $rng);\r\n		} else {\r\n			$retarr[] = (($negate) ? "!" : "") . $item;\r\n		}\r\n	}\r\n	return $retarr;\r\n}\r\n}\r\n\r\nif (!function_exists("smd_doDblQuote")) {\r\nfunction smd_doDblQuote($val) {\r\n	return ''"''.$val.''"'';\r\n}\r\n}\r\n\r\nif (!function_exists("smd_removeQSVar")) {\r\nfunction smd_removeQSVar($url, $key) {\r\n	$url = preg_replace(''/(.*)(\\?|&)'' . $key . ''=[^&]+?(&)(.*)/i'', ''$1$2$4'', $url . ''&'');\r\n	$url = substr($url, 0, -1);\r\n	return ($url);\r\n}\r\n}\r\n\r\nif (!function_exists("smd_addQSVar")) {\r\nfunction smd_addQSVar($url, $key, $value) {\r\n	$url = smd_removeQSVar($url, $key);\r\n	if (strpos($url, ''?'') === false) {\r\n		return ($url . ''?'' . $key . ''='' . $value);\r\n	} else {\r\n		return ($url . ''&'' . $key . ''='' . $value);\r\n	}\r\n}\r\n}\r\n\r\n// DEPRECATED: for backwards compatibility only\r\nif (!function_exists("smd_getSubCats")) {\r\nfunction smd_getSubCats($parent,$cattype) {\r\n	return getTree($parent,$cattype); //getTree() or getTreePath()??\r\n}\r\n}\r\n\r\n// DEPRECATED: for backwards compatibility only\r\nif (!function_exists("smd_getOpts")) {\r\nfunction smd_getOpts($str, $allowed, $idprefix="", $allowRange=true, $splitat="/(,|,\\s)+/", $pregopt = PREG_SPLIT_NO_EMPTY) {\r\n	global $pretext, $thisarticle;\r\n\r\n	$out = array();\r\n	$notout = array();\r\n	$matches = smd_split($str, $allowRange, $splitat, $pregopt);\r\n	// An array that tells the loop what to do with each of the valid strings\r\n	//  arg1: type (1=exact match, 2=anywhere within string, 3=custom field)\r\n	//  arg2: prefix, if any\r\n	//  arg3: variable to substitute\r\n	//  arg4: extra check, if applicable\r\n	//  arg5: store in the in/exclude list (1=include; 2=exclude)\r\n	$opt = array(\r\n		"?c" => array(2, "", $pretext[''c''], "1", 1),\r\n		"!c" => array(2, "", $pretext[''c''], "1", 2),\r\n		"?s" => array(2, "", $pretext[''s''], "1", 1),\r\n		"!s" => array(2, "", $pretext[''s''], "1", 2),\r\n		"?q" => array(2, "", $pretext[''q''], "1", 1),\r\n		"!q" => array(2, "", $pretext[''q''], "1", 2),\r\n		"?t" => array(2, "", $thisarticle[''url_title''], ''$thisarticle!=NULL'', 1),\r\n		"!t" => array(2, "", $thisarticle[''url_title''], ''$thisarticle!=NULL'', 2),\r\n		"?id" => array(1, $idprefix, $pretext[''id''], ''$thisarticle!=NULL'', 1),\r\n		"!id" => array(1, $idprefix, $pretext[''id''], ''$thisarticle!=NULL'', 2),\r\n		"?" => array(3, "", "", ''$thisarticle!=NULL'', 1),\r\n		"!" => array(3, "", "", ''$thisarticle!=NULL'', 2),\r\n	);\r\n\r\n	for ($idx = 0; $idx < count($matches); $idx++) {\r\n		$matched = false;\r\n		$thismatch = $matches[$idx];\r\n		foreach ($opt as $var => $args) {\r\n			$opvar = ($args[4] == 1) ? "out" : "notout";\r\n			switch ($args[0]) {\r\n				case 1:\r\n					if (($thismatch === $var) && in_array($var,$allowed)) {\r\n						$matched = true;\r\n						if ($args[2] != "" && $args[3]) {\r\n							$rep = str_replace($var, $args[1].$args[2], $thismatch);\r\n							if (!in_array($rep, ${$opvar})) {\r\n								${$opvar}[] = $rep;\r\n							}\r\n						}\r\n					}\r\n					break;\r\n				case 2:\r\n					$pat = ''/\\\\''.$var.''$|\\\\''.$var.''[^A-Za-z0-9]/'';\r\n					if ((is_int(smd_pregPos($pat, $thismatch, $fnd))) && in_array($var,$allowed)) {\r\n						$matched = true;\r\n						if ($args[2] != "" && $args[3]) {\r\n							$rep = str_replace($var, $args[1].$args[2], $thismatch);\r\n							if (!in_array($rep, ${$opvar})) {\r\n								${$opvar}[] = $rep;\r\n							}\r\n						}\r\n					}\r\n					break;\r\n				case 3:\r\n					$len = strlen($var);\r\n					if ((substr($thismatch,0,$len) === $var) && in_array($var."field",$allowed)) {\r\n						$matched = true;\r\n						// Use the given field name; which may be a comma-separated sublist.\r\n						// Split off the field name from the question mark\r\n						$fieldname = substr($thismatch,$len);\r\n						if (($args[3]) && (isset($thisarticle[strtolower($fieldname)]))) {\r\n							$fieldContents = $thisarticle[strtolower($fieldname)];\r\n						} else {\r\n							$fieldContents = $fieldname;\r\n						}\r\n						if (!empty($fieldContents)) {\r\n							$subout = smd_split(strip_tags($fieldContents), $allowRange, $splitat, $pregopt);\r\n							foreach ($subout as $subname) {\r\n								if (!in_array($subname, ${$opvar})) {\r\n									${$opvar}[] = $subname;\r\n								}\r\n							}\r\n						}\r\n					}\r\n					break;\r\n			}\r\n			if ($matched) {\r\n				break;\r\n			}\r\n		}\r\n		if (!$matched) {\r\n			// Assign the variable verbatim\r\n			if (!in_array($thismatch, $out)) {\r\n				$out[] = $thismatch;\r\n			}\r\n		}\r\n	}\r\n	return array($out,$notout);\r\n}\r\n}\r\n\r\n// Stolen from php.net: strpos page comments...\r\nif (!function_exists("smd_pregPos")) {\r\nfunction smd_pregPos($sPattern, $sSubject, &$FoundString, $iOffset = 0) {\r\n	$FoundString = null;\r\n	if (preg_match($sPattern, $sSubject, $aMatches, PREG_OFFSET_CAPTURE, $iOffset) > 0) {\r\n		$FoundString = $aMatches[0][0];\r\n		return $aMatches[0][1];\r\n	} else {\r\n		return false;\r\n	}\r\n}\r\n}\r\n//... and array_combine...\r\nif (!function_exists("array_combine")) {\r\n	function array_combine($arr1,$arr2) {\r\n		$out = array();\r\n		foreach($arr1 as $key1 => $value1) {\r\n			$out[$value1] = $arr2[$key1];\r\n		}\r\n   	return $out;\r\n	}\r\n}\r\n//... and htmlspecialchars_decode\r\nif (!function_exists("htmlspecialchars_decode")) {\r\n	function htmlspecialchars_decode($string, $quote_style = ENT_COMPAT) {\r\n		return strtr($string, array_flip(get_html_translation_table(HTML_SPECIALCHARS, $quote_style)));\r\n	}\r\n}', 'c2da3c9dcce2ec98749fe95b47e8475e', 2, 2, 0);
INSERT INTO `txp_plugin` (`name`, `status`, `author`, `author_uri`, `version`, `description`, `help`, `code`, `code_restore`, `code_md5`, `type`, `load_order`, `flags`) VALUES
('smd_gallery', 1, 'Stef Dawson', 'http://stefdawson.com/', '0.62', 'A(nother) thumbnail gallery/slideshow creator with unreal flexibility', '<div id="smd_help">\n\n	<h1 id="top">smd_gallery</h1>\n\n	<h2 id="features">Features</h2>\n\n	<ul>\n		<li>Supports all current and (probably) future gallery scripts including thumbpop, rollover, tooltip, lightbox, litebox, slimbox, moonbox, thickbox, slideshow, fancyzoom, etc</li>\n		<li>Pull images from the <span class="caps">TXP</span> database that match categories, IDs, author, extension or particular dimensions / aspect ratios</li>\n		<li>Use article fields to hold lists of image categories/IDs for article-specific galleries</li>\n		<li>Multi-level sub-category support</li>\n		<li>Gallery layout is defined in a <span class="caps">TXP</span> form/container</li>\n		<li>Direct-access pagination is supported and the layout of paging features can be controlled by a second form</li>\n		<li>Uses a tag-replacement system like the wet_for_each_image plugin</li>\n		<li><span class="caps">MLP</span> support</li>\n	</ul>\n\n	<h2 id="author">Author</h2>\n\n	<p><a href="http://stefdawson.com/contact">Stef Dawson</a>. For other software by me, or to make a donation, see the <a href="http://stefdawson.com/sw">software page</a>.</p>\n\n	<h2 id="install">Installation / Uninstallation</h2>\n\n	<p class="required">Requires <span class="caps">TXP</span> 4.2.0+ and smd_lib v0.36+</p>\n\n	<p>Download the plugin from either <a href="http://textpattern.org/plugins/957/smd_gallery">textpattern.org</a>, or the software page above, paste the code into the <span class="caps">TXP</span> Admin -&gt; Plugins pane, install and enable the plugin. Visit the <a href="http://forum.textpattern.com/viewtopic.php?id=26482">forum thread</a> for more info or to report on the success or otherwise of the plugin.</p>\n\n	<p>To remove the plugin, simply delete it from the Admin-&gt;Plugins tab. If no other plugins are using smd_lib you may safely delete that too.</p>\n\n	<h2 id="usage">Overview</h2>\n\n	<p>The general idea is that this plugin acts as a flexible image filter; nothing more. The plugin options govern which images are pulled out of the database and they are sorted into whatever order you like.</p>\n\n	<p>After that, each image &#8212; one by one &#8212; has all its attributes passed through the container or <code>form</code> of your choice; you simply choose to display whichever image attributes you like in this form. This means any image attributes can be &#8220;injected&#8221; directly into any markup; it could be <span class="caps">TXP</span> tags, it could be content, it could be javascript, or <span class="caps">CSS</span>; anything.</p>\n\n	<p>Pagination works the same way, allowing you to specify how users can step through your image galleries.</p>\n\n	<p>There is also a special &#8220;collate&#8221; mode that allows you to collect all image attributes together into a series of big long lists <em>without</em> calling the form every time. The form/container is then only processed once at the end.</p>\n\n	<p>Between these two modes you should be able to realise any gallerying solution; now and in future.</p>\n\n	<h2 class="tag " id="smd_gallery">smd_gallery</h2>\n\n	<p class="tag-summary">Place one or more smd_gallery tags in an article, page or form, and supply any of the following options to configure it. The attributes are grouped by function to make it easier on your eyes / sanity.</p>\n\n	<h3 id="attributes">Attributes</h3>\n\n	<h4>Filters</h4>\n\n	<ul>\n		<li><span class="atnm">id</span> : list of image IDs to display. May be integers or ranges, for example <code>id=&quot;5, 42, 8-11&quot;</code> would grab images 5, 8, 9, 10, 11 and 42. Use &#8216;!&#8217; in front of a number or range to exclude those images. Use &#8216;?&#8217; and the name of a <span class="caps">TXP</span> field to read the list of image IDs from that field, e.g. <code>id=?article_image&quot;</code></li>\n		<li><span class="atnm">name</span> : list of image names to display. You can either specify exact matches, e.g. <code>name=&quot;lion.jpg, tiger.jpg&quot;</code> or regular expression matches by appending <code>:regex</code> to terms. For example, <code>name=&quot;lion:regex&quot;</code> would match lion.jpg, lioness.jpg and megalion.jpg. Alternatively, <code>name=&quot;^lion:regex&quot;</code> would only match images that start with &#8216;lion&#8217;. Use &#8216;!&#8217; in front of names to exclude those images. Use &#8216;?&#8217; and the name of a <span class="caps">TXP</span> field to read the list of image names/regexes from that field, e.g. <code>name=?filenames&quot;</code> to read them from a custom field of that name.<div class="break">If you want to negate a regular expression, things may get tricky if you are using meta characters; in this case it is better to set your regular expression up in, say, a txp:variable (or an article field) and use <code>name=&quot;!var_name:regex&quot;</code>.</div><div class="break">You can mix exact matches and regex matches in the same <code>name</code> attribute if you wish. The colon can be altered with <code>paramdelim</code> &#8212; this is sometimes necessary if you are using character classes such as <code>[[:digit:]]</code> in your regular expression.</div></li>\n		<li><span class="atnm">category</span> : list of image categories to display. You may specify fixed-name categories (e.g. &#8220;animals&#8221;) or a&#8217;?&#8217; followed by an article field name. e.g. <code>category=&quot;animals, art-?thisid&quot;</code> would grab all images from category &#8220;animals&#8221; and category &#8220;art-42&#8221; (assuming the current article has an id of 42). Use &#8216;!&#8217; to <em>exclude</em> the given category or field.<div class="break">Fields may themselves contain lists of categories. The &#8216;?&#8217; field names may be combined (e.g. ?s-?article_image) but be aware that custom field names may contain spaces if <code>strictfields=&quot;0&quot;</code></div></li>\n		<li><span class="atnm">sublevel</span> : an integer that specifies how many &#8216;levels&#8217; down to traverse each matching category. <code>sublevel=&quot;0&quot;</code> (the default) is only the top-level; 1 = top-level plus the 1st sub-cat; 2 = top-level plus the next 2 sub-cats, and so on. <code>sublevel=&quot;all&quot;</code> is all sub-cats</li>\n		<li><span class="atnm">author</span> : filter images by this list of author login names</li>\n		<li><span class="atnm">extension</span> : filter images by this list of file extensions (without the leading dot)</li>\n		<li><span class="atnm">aspect</span> : filter images by this list of aspect ratios. Choose from any of <code>portrait</code>, <code>landscape</code> or <code>square</code></li>\n		<li><span class="atnm">dimensions</span> : filter images matching the given dimensions. Specify up to two values. The first is the width and the second is the height. If you wish to specify minimum or maximum dimensions, add <code>&gt;</code>, <code>&gt;=</code>, <code>&lt;</code> or <code>&lt;=</code> before the value. For example, <code>dimensions=&quot;&gt;=1024, &gt;=768&quot;</code> would only show images greater than or equal to 1024 &#215; 768 pixels</li>\n		<li><span class="atnm">sort</span> : re-order the images by <code>category</code> (the default), <code>name</code>, <code>date</code>, <code>id</code> (the default if using an id), file <code>ext</code>ension, <code>alt</code>, <code>caption</code>, <code>author</code>, <code>width</code>, <code>height</code>, <code>thumb_width</code>, <code>thumb_height</code>, <code>hasthumb</code> in either <code>asc</code>ending or <code>desc</code>ending order. Two special cases exist: <code>fixed</code> maintains the order you specified in the id and/or category attribute, while <code>random</code> shuffles the order.<div class="break">Note that if <code>random</code> appears as the first item in the list, random images will be pulled from the database and they will then be sorted according to the order listed after it. If <code>random</code> appears anywhere else in the list, any sort items before it will determine the order of data pulled from the database, and the results will then be ordered randomly. In the 2nd case, any items listed after &#8216;random&#8217; will be ignored, but <code>fixed</code> is still honoured</div></li>\n	</ul>\n\n	<h4>Limits / offsets / sizes</h4>\n\n	<ul>\n		<li><span class="atnm">limit</span> : the maximum number of images to show <em>per page</em>. 0 (the default) or omitted = unlimited</li>\n		<li><span class="atnm">maxlimit</span> : the absolute maximum number of image to pull from the database. Must be greater than 0</li>\n		<li><span class="atnm">offset</span> : skip this many images and start displaying them thereafter</li>\n		<li><span class="atnm">thumblimit</span> : the maximum number of thumbnails to show. 0 or omitted = all thumbs. Note that this differs from limit; you can launch a 20-image gallery but only show 1 thumbnail on the page. Be aware that if you are showing multiple categories from a single tag, you will see <code>thumblimit</code> thumbnails <em>at each change in category</em>. If you don&#8217;t want this, set <code>onchange</code> to something else. See the <code>{object}</code> <a href="#rep_gallery">replacement tag</a></li>\n		<li><span class="atnm">grouplimit</span> : the maximum number of images to put on the page, <em>per group</em>. A group is considered any set of images (e.g. all images from one category) so this can be used to only display N images at each change in category. A concrete example is displaying a thumbnail that launches a gallery showing the 5 most recent works from each author. Without <code>thumblimit=&quot;1&quot; grouplimit=&quot;5&quot;</code> the number of images in the group would be unlimited. See <a href="#eg12">example 12</a> for more</li>\n		<li><span class="atnm">thumboffset</span> : skip this many thumbs before displaying <code>thumblimit</code> thumbnails</li>\n		<li><span class="atnm">thumbsize</span> : takes one or two comma-separated numbers to specify width and/or height of all your gallery thumbnails. The default is in pixels if you don&#8217;t specify units. Missing out one or the other parameter (i.e. having an &#8220;empty&#8221; parameter before or after the comma) will only set one of the dimensions. Use a single value to make square thumbs. If you omit either or both, the corresponding value will be read from the database</li>\n		<li><span class="atnm">imgsize</span> : identical to <code>thumbsize</code> but for the full size images</li>\n	</ul>\n\n	<h4>Forms and paging</h4>\n\n	<ul>\n		<li><span class="atnm">form</span> : the <span class="caps">TXP</span> form used to layout your matching images. Without this attribute the plugin acts as a container tag. Without a container, the plugin displays each full-size image, hyperlinked</li>\n		<li><span class="atnm">pageform</span> : the <span class="caps">TXP</span> form used to specify the layout of your paging navigation</li>\n		<li><span class="atnm">paging</span> : switch paging on without having to specify a pageform. When this is used, all <a href="#rep_paging">page replacement tags</a> become available in the <a href="#rep_gallery">gallery replacement tags</a> as well</li>\n		<li><span class="atnm">pagepos</span> : the position of the paging block. Options are <code>below</code> (the default), <code>above</code>, or both of them separated by <code>delim</code></li>\n		<li><span class="atnm">objectform</span> : the <span class="caps">TXP</span> form used to specify the layout of your <code>{object}</code> replacement tag. You can use all the standard <a href="#rep_gallery">gallery replacement tags</a> with the exception of <code>{object}</code> itself! Uses a hyperlinked image if no form is specified</li>\n		<li><span class="atnm">countform</span> : list of <span class="caps">TXP</span> forms to use when certain numbered images are reached. See <a href="#countform">count forms</a> and <a href="#eg10">example 10</a>. This has no effect in collate mode</li>\n		<li><span class="atnm">countpos</span> : default position of the <code>countform</code>s. Either <code>above</code> (the default), <code>below</code>, or both of them separated by <code>delim</code>. Can be overridden on a per-form basis</li>\n	</ul>\n\n	<h4>Grouping images</h4>\n\n	<ul>\n		<li><span class="atnm">onchange</span> : a list of attributes to &#8220;watch&#8221;. It tells the plugin that you want to be notified when items in this list change compared to the previous image. Under normal circumstances this should be the same as your <code>sort</code> parameter or you may get some freaky results! It defaults to <code>category</code>.<div class="break">This is very useful for splitting galleries at every change in category and displaying the category title above each group. Items you watch are added to the list of replacement tags and are available by putting &#8220;onchange:&#8221; in front of their name (e.g. <code>{onchange:category}</code> &#8212; see <a href="#eg6">example 6</a> for more). The colon delimiter can be changed with <code>paramdelim</code>.</div><div class="break">You can also elect to defer processing of onchange events to a form. To do so, specify a form after your watch item like this <code>category_title:my_form</code>. In my_form you can put any markup and replacement tags. See <a href="#eg11">example 11</a> for more.</div><div class="break">Another way of using this attribute is with the special prefix <code>smd_every:n</code> which will trigger a group change every Nth image. Note it can&#8217;t be used to watch specific things in this mode; it is only used to trigger <code>grouptag</code> changes</div></li>\n		<li><span class="atnm">onchangewraptag</span> : the (X)HTML tag to surround any <code>onchange</code> elements you are watching, e.g. <code>onchangewraptag=&quot;h3&quot;</code></li>\n		<li><span class="atnm">onchangeclass</span> : the <span class="caps">CSS</span> class name tro apply to the wraptag of any onchange elements you are watching</li>\n		<li><span class="atnm">grouptag</span> : the (X)HTML tag to make available as a replacement so you can wrap groups of elements at each <code>onchange</code>. Default: unset. See <a href="#eg6">examples 6</a> for more on the <code>grouptagstart</code> / <code>grouptagend</code> replacement tags</li>\n		<li><span class="atnm">groupclass</span> : the <span class="caps">CSS</span> class name applied to any <code>grouptag</code></li>\n	</ul>\n\n	<h4>Tag/class/formatting attributes</h4>\n\n	<ul>\n		<li><span class="atnm">wraptag</span> : the (X)HTML tag to surround the entire gallery, e.g. <code>wraptag=&quot;div&quot;</code></li>\n		<li><span class="atnm">break</span> : the (X)HTML tag to surround each thumbnail</li>\n		<li><span class="atnm">class</span> : the <span class="caps">CSS</span> class name applied to the <code>wraptag</code></li>\n		<li><span class="atnm">breakclass</span> : the <span class="caps">CSS</span> class name applied to the <code>break</code> elements</li>\n		<li><span class="atnm">html_id</span> : the name of an <span class="caps">HTML</span> <code>id</code> attribute to apply to the wraptag</li>\n		<li><span class="atnm">navwraptag</span> : the (X)HTML tag to surround the entire <code>pageform</code>, e.g. <code>wraptag=&quot;div&quot;</code>. Default: <code>ul</code></li>\n		<li><span class="atnm">navbreak</span> : the (X)HTML tag to surround each item within the <code>pageform</code>. Default: <code>li</code></li>\n		<li><span class="atnm">navclass</span> : the <span class="caps">CSS</span> class name of the navigation wraptag. Default: <code>smd_gallery_nav</code></li>\n		<li><span class="atnm">emptyclass</span> : the <span class="caps">CSS</span> class name of the navigation items that are <em>not</em> links. Default: <code>empty</code></li>\n		<li><span class="atnm">format</span> (<span class="required">only if using <code>{date}</code></span>) : allows you to change the date format used. Can take any <a href="http://uk.php.net/strftime">strftime()</a> values. Defaults to the date system as set in the site&#8217;s prefs</li>\n		<li><span class="atnm">lang</span> (<span class="required">only if using <code>{date}</code></span>) : sets the language code of the date format</li>\n		<li><span class="atnm">gmt</span> (<span class="required">only if using <code>{date}</code></span>) : returns either local time according to the set time zone preferences (0), or <span class="caps">GMT</span> time (1). Default: 0</li>\n	</ul>\n\n	<h4>Plugin customisation</h4>\n\n	<ul>\n		<li><span class="atnm">delim</span> : change the delimiter for specifying attribute lists. Default is comma (,)</li>\n		<li><span class="atnm">paramdelim</span> : change the delimiter for specifying inter-parameter attributes. Default is colon (:)</li>\n		<li><span class="atnm">collate</span> : switch the plugin into <a href="#collate_mode">collate mode</a></li>\n		<li><span class="atnm">collatedelim</span> : change the delimiter between each collated item. Default is comma (,)</li>\n		<li><span class="atnm">combo</span> : specify your own <a href="#combos">custom replacement tags</a></li>\n		<li><span class="atnm">escape</span> : list of replacement variable names to &#8216;escape&#8217;, in other words convert any <span class="caps">HTML</span> tags to their character entity equivalents. Set to <code>escape=&quot;&quot;</code> to turn this feature off. You can escape your own combos. Default: <code>alt, title, caption</code></li>\n		<li><span class="atnm">strictfields</span> : when using &#8216;?&#8217; or &#8216;!&#8217; fields, spaces are allowed in field names. Set <code>strictfields=&quot;1&quot;</code> to forbid spaces. Default: 0</li>\n		<li><span class="atnm">galleryid</span> : under normal circumstances you should never need this. Each gallery is assigned a unique ID based on the supplied attributes so you may use more than one gallery on a page and navigate them independently. In the unlikely event that two identical IDs are generated you can override the ID for a gallery by using this attribute.<div class="break">Internally the plugin generates a 32-character hash string for each tag, then picks a 4-char code from this string (it takes every 6th character by default). You can supply a unique fixed string (e.g. <code>galleryid=&quot;MyGal&quot;</code>) or give it a question mark and 2 numbers separated by a colon (e.g. <code>galleryid=&quot;?5:4&quot;</code>). The first number is the length of the final string to generate, the second number is how many characters to skip in the 32-char string each time it picks one.</div><div class="break">In this case it generates a 5-character string from the characters it finds at positions 1,5,9,13, and 17. If you omit the 2nd number (e.g. <code>galleryid=&quot;?6&quot;</code>) it will assume it&#8217;s an increment of 1</div></li>\n		<li><span class="atnm">thumb</span> : under normal operation, thumbnails use the &#8216;t&#8217; image in your <code>images</code> directory and the full size pictures don&#8217;t. If you wish to redefine this you may specify up to two values separated by a colon. The first indicates the state of the full size image, and the second is the state of the thumbnail:\n	<ul>\n		<li>thumb=0:1 &#8212; fullsize = regular image : thumbnail = thumb</li>\n		<li>thumb=1:0 &#8212; fullsize = thumb : thumbnail = regular image</li>\n		<li>thumb=0:0 (or thumb=0) &#8212; fullsize = regular image : thumbnail = regular image</li>\n		<li>thumb=1:1 (or thumb=1) &#8212; fullsize = thumb : thumbnail = thumb</li>\n	</ul></li>\n		<li><span class="atnm">text</span> : display the given text instead of thumbnails. The text can contain <code>?</code> article fields</li>\n	</ul>\n\n	<h4>Special attributes</h4>\n\n	<ul>\n		<li><span class="atnm">columns</span> : give a comma-separated list of additional columns you would like to pull from the database. Each item you specify is split into three parts: <code>table.column:replacement name</code>. The only mandatory part is the replacement name, since <code>table</code> defaults to txp_image, and the <code>column</code> defaults to the same name as the replacement you specify.<div class="break">For example, if you have installed the cno_image_order plugin you could use <code>columns=&quot;position&quot;</code>, since the plugin adds the &#8216;position&#8217; column to txp_image. You could then sort by <code>position</code> and also display the image&#8217;s <code>{position}</code>. If you wished to rename the variable to, say, <code>imgpos</code> you would use <code>columns=&quot;position:imgpos&quot;</code>. Note that if you specify the table name you must also include the table prefix if you have used one.</div></li>\n		<li><span class="atnm">directory</span> : (<span class="required">experimental</span>) read images from a list of directories (or <span class="caps">URL</span>s) instead of the <span class="caps">TXP</span> database</li>\n		<li><span class="atnm">match</span> : limit the images and directories chosen in <code>directory</code> mode. Give a list of words to include or !exclude and any matching files or directories will be chosen or ignored, respectively. The match terms are automatically assumed to be &#8220;wild&#8221; so any part of the file name / path will trigger the match</li>\n		<li><span class="atnm">debug</span> : turn on diagnostics. Varying levels of verbosity from 0 (none) to 4 (a lot)</li>\n	</ul>\n\n	<h2 id="gallery_form">Gallery form</h2>\n\n	<p>The <code>form</code> is where you design what every thumbnail will look like on the screen. You do this by specifying <span class="caps">HTML</span> markup or <span class="caps">TXP</span> tags as normal. In key places where you want, for example, an image ID / alt tag / image width to be displayed, you specify special replacement tags, detailed below. These are replaced for each image in turn, thus building up the gallery.</p>\n\n	<p>In collate mode, the image details are instead made into lists. The form is only processed once and the replacement tags output the entire list.</p>\n\n	<h3 class="atts " id="rep_gallery">Replacement tags: gallery</h3>\n\n	<ul>\n		<li><span class="atnm">{id}</span> : image ID (or, in directory mode, the filename without its extension)</li>\n		<li><span class="atnm">{name}</span> : full image filename as defined in the database, usually including extension</li>\n		<li><span class="atnm">{ext}</span> : file extension, including dot</li>\n		<li><span class="atnm">{category}</span> : image category name</li>\n		<li><span class="atnm">{category_title}</span> : image category title</li>\n		<li><span class="atnm">{author}</span> : who uploaded the image</li>\n		<li><span class="atnm">{url}</span> : <span class="caps">URL</span> of the fullsize image</li>\n		<li><span class="atnm">{thumburl}</span> : <span class="caps">URL</span> of the thumbnail</li>\n		<li><span class="atnm">{imagepath}</span> : directory the image is in</li>\n		<li><span class="atnm">{imagedef}</span> : just the file name part of the image (e.g. 5.jpg)</li>\n		<li><span class="atnm">{thumbdef}</span> : just the file name part of the thumbnail (e.g. 5t.jpg)</li>\n		<li><span class="atnm">{alt}</span> : alt text</li>\n		<li><span class="atnm">{title} or {caption}</span> : caption</li>\n		<li><span class="atnm">{date}</span> : image upload date</li>\n		<li><span class="atnm">{width}</span> : image width</li>\n		<li><span class="atnm">{height}</span> : image height</li>\n		<li><span class="atnm">{wunits}</span> : image width units (e.g. px, %, em, etc.)</li>\n		<li><span class="atnm">{hunits}</span> : image height units</li>\n		<li><span class="atnm">{thumbwidth}</span> : thumbnail width</li>\n		<li><span class="atnm">{thumbheight}</span> : thumbnail height</li>\n		<li><span class="atnm">{thumbwunits}</span> : thumbnail width units (e.g. px, %, em, etc.)</li>\n		<li><span class="atnm">{thumbhunits}</span> : thumbnail height units</li>\n		<li><span class="atnm">{hasthumb}</span> : if the image has an associated thumbnail or not</li>\n		<li><span class="atnm">{galleryid}</span> : gallery id that you or the plugin assigned</li>\n		<li><span class="atnm">{object}</span> : a special item containing either the <code>text</code> OR the image tag of the corresponding thumbnail, but <em>only</em> if <code>thumblimit</code> allows it. e.g. if <code>thumblimit=&quot;1&quot;</code> this variable is only set for the first thumbnail in the list; every other time it is empty</li>\n		<li><span class="atnm">{counter}</span> : image counter, starting from 1 and incrementing each time</li>\n		<li><span class="atnm">{index}</span> : index counter (zero-based &#8212; probably not much use)</li>\n		<li><span class="atnm">{thumbcounter}</span> : thumbnail image counter, starting from 1. Note it is &#8216;reset&#8217; whenever an element given in <code>onchange</code> differs from the previous item</li>\n		<li><span class="atnm">{thumbindex}</span> : index counter (zero-based &#8212; probably not much use). It is reset like <code>{thumbcounter}</code></li>\n		<li><span class="atnm">{totalimages}</span> : total number of images matched on this page</li>\n		<li><span class="atnm">{maximages}</span> : absolute maximum number of images across all pages</li>\n	</ul>\n\n	<p>In addition, if either <code>paging=&quot;1&quot;</code> or <code>pageform</code> have been specified, all the <a href="#rep_paging">paging replacement tags</a> are available too. If those aren&#8217;t enough, you can <a href="#combos">create your own</a> by grouping combinations of the above, optionally with article variables such as <code>?excerpt</code>, <code>?keywords</code>, <code>?article_image</code>, etc.</p>\n\n	<p>Other tags are added depending if you are watching for <code>onchange</code> elements, or if you have specified a <code>grouptag</code>. If you added <code>onchange=&quot;author, category&quot;</code> then every time the author or category changed compared to the previous image, one or two new replacement tags would become available; namely <code>{onchange:author}</code> and <code>{onchange:category)</code>. Using onchange with <code>smd_every</code> does <em>not</em> add a new replacement tag as it is merely used to count.</p>\n\n	<p>If you used <code>grouptag=&quot;div&quot;</code> as well, you would be given two new replacement tags labelled <code>grouptagstart</code> and <code>grouptagend</code>. See <a href="#eg6">example 6</a> for details.</p>\n\n	<h2 id="paging_form">Paging form</h2>\n\n	<p>Add gallery navigation to your page. Works in a similar manner to the <a href="#gallery_form">gallery form</a>: a series of replacement tags govern which gallery navigation items are available. You could choose to only show next/prev. Or perhaps 1st page/last page. Or numbered page links.</p>\n\n	<h3 class="atts " id="rep_paging">Replacement tags: paging</h3>\n\n	<ul>\n		<li><span class="atnm">{navfirst}</span> : first page (default label: &#8220;First&#8221; / <span class="caps">MLP</span>)</li>\n		<li><span class="atnm">{navprev}</span> : previous page (default label: &#8220;&laquo;&#8221; / <span class="caps">MLP</span>)</li>\n		<li><span class="atnm">{navearlier}</span> : any page numbers before the current page</li>\n		<li><span class="atnm">{navthisurl}</span> : current page with a <span class="caps">URL</span> link to itself</li>\n		<li><span class="atnm">{navthis}</span> : current page without a <span class="caps">URL</span> link</li>\n		<li><span class="atnm">{navlater}</span> : any page numbers after the current page</li>\n		<li><span class="atnm">{navnext}</span> : next page (default label: &#8220;&raquo;&#8221; / <span class="caps">MLP</span>)</li>\n		<li><span class="atnm">{navlast}</span> : last page (default label: &#8220;Last&#8221; / <span class="caps">MLP</span>)</li>\n		<li><span class="atnm">{navfirstpageurl}</span> : raw <span class="caps">URL</span> to first page</li>\n		<li><span class="atnm">{navprevpageurl}</span> : raw <span class="caps">URL</span> to previous page</li>\n		<li><span class="atnm">{navthispageurl}</span> : raw <span class="caps">URL</span> to current page</li>\n		<li><span class="atnm">{navnextpageurl}</span> : raw <span class="caps">URL</span> to next page</li>\n		<li><span class="atnm">{navlastpageurl}</span> : raw <span class="caps">URL</span> to last page</li>\n		<li><span class="atnm">{totalimages}</span> : total number of images matched on the current page</li>\n		<li><span class="atnm">{totalpages}</span> : total number of pages in this collection</li>\n		<li><span class="atnm">{maximages}</span> : absolute maximum number of images across all pages</li>\n	</ul>\n\n	<p>The current page has a fixed class of &#8216;current&#8217; and each item is, by default, a <code>&lt;li&gt;</code> for easy styling. Custom labels are defined directly in the form itself; for example to label the link that takes users to the first page of the gallery as &#8220;1st&#8221;, use:</p>\n\n	<p><code>{navfirst:1st}</code></p>\n\n	<p>With this syntax you can label navfirst, navlast, navprev, navnext and navthis (the &#8216;:&#8217; delimiter in this case is specified by <code>paramdelim</code>). You can use this mechanism to read <span class="caps">MLP</span> snippets, e.g:</p>\n\n	<p><code>{navfirst:##firstLabel##}</code></p>\n\n	<p>You are not limited to text labels. Images could be used:</p>\n\n	<p><code>{navlast: &lt;img src=&quot;images/last.png&quot; /&gt;}</code></p>\n\n	<p>You can override the number of items displayed by navearlier and navlater by specifying a colon and a number. So to only show 2 items to the left of the current page and 3 items to the right, use <code>{navearlier:2} {navlater:3}</code> in your form. If you were on page 9 of 15, {navearlier:4} {navlater:3} would show <code>&lt;&lt; 5 6 7 8 9 10 11 12 &gt;&gt;</code></p>\n\n	<p>Currently <code>{navearlier}</code> and <code>{navlater}</code> only display arabic numerals. Any ideas on how to offer other character sets greatly appreciated.</p>\n\n	<h2 id="countform">Count forms</h2>\n\n	<p>Sometimes it&#8217;s handy to be able to take action only when certain images are reached; for example:</p>\n\n	<ul>\n		<li>only display something on the first or last image</li>\n		<li>start a new <span class="caps">HTML</span> &#8216;row&#8217; every 3rd image</li>\n		<li>break your gallery up with an advert</li>\n	</ul>\n\n	<p>This can be done with smd_if but since it is such a common feature, the plugin has the ability built in. All replacement tags are available within your countforms so you can do some pretty neat stuff. It is best explained by example:</p>\n\n	<ul>\n		<li><code>countform=&quot;1:gal_head&quot;</code> : this would output the form <em>gal_head</em> only when the first image is displayed</li>\n		<li><code>countform=&quot;{totalimages}:gal_foot&quot;</code> : this would output the form <em>gal_foot</em> only when the last image is displayed</li>\n		<li><code>countform=&quot;1:gal_head, {totalimages}:gal_foot&quot;</code> : do both of the above</li>\n		<li><code>countform=&quot;1:gal_head, {totalimages}:gal_foot:smd_below&quot;</code> : the same as the previous example, but put the contents below the last image (by default, all output appears above the given image)</li>\n		<li><code>countform=&quot;4:9:smd_below:gal_ad&quot;</code> : output <em>gal_ad</em> after the 4th and 9th images only</li>\n		<li><code>countform=&quot;4-9:smd_below:gal_ad&quot;</code> : output <em>gal_ad</em> after the 4th, 5th, 6th, 7th, 8th and 9th images only</li>\n		<li><code>countform=&quot;smd_every:3:gal_row&quot;</code> : output <em>gal_row</em> before every 3rd image</li>\n		<li><code>countform=&quot;smd_every:2:!8:gal_row&quot;</code> : output <em>gal_row</em> before every even image but not on the 8th image</li>\n		<li><code>countform=&quot;:gal_row&quot;</code> : use an empty first value to output <em>gal_row</em> before every image. This may seem useless, but it means you can use two, three, four&#8230; forms (or any number of forms and the container) simultaneously to iterate over the same gallery. Or use the same form at different frequencies. Get creative!</li>\n		<li><code>countform=&quot;:!5:gal_row:smd_below&quot;</code> : again, missing out the first value will output gal_row after every image, but this time skipping the 5th image</li>\n	</ul>\n\n	<p>The <code>,</code> and <code>:</code> can be overridden with the <code>delim</code> and <code>paramdelim</code> attributes, respectively.</p>\n\n	<p>There are a few rules when using the countform:</p>\n\n	<ul>\n		<li>numbers must appear before the form name</li>\n		<li>the form name is usually the last item in the colon-separated list, though you can put <code>smd_above</code> or <code>smd_below</code> last if you wish</li>\n		<li><code>smd_every</code> only affects the value that <em>immediately</em> follows it, e.g. <code>countform=&quot;smd_every:5:7:12:my_ad&quot;</code> will output <em>my_ad</em> at every 5th image, as well as <em>only</em> on the 7th and 12th images. Thus the sequence would be 5, 7, 10, 12, 15, 20, 25, 30&#8230;</li>\n		<li>if using <code>smd_every</code>, it <strong>must</strong> appear as the first item. For example <code>countform=&quot;4:the_fourth, 6:smd_every:5:my_ad&quot;</code> is invalid, but <code>countform=&quot;4:the_fourth, smd_every:5:6:my_ad&quot;</code> is OK</li>\n	</ul>\n\n	<p>See <a href="#eg10">example 10</a> for more.</p>\n\n	<h2 id="combos">Combos</h2>\n\n	<p>If the built-in <a href="#rep_gallery">replacement tags</a> aren&#8217;t enough for your needs or you find yourself repeating the same combination and want to save yourself some typing, you can define your own. The syntax is:</p>\n\n	<p><code>combo=&quot;newtag1:definition, newtag2:definition, ...&quot;</code></p>\n\n	<p>&#8216;definition&#8217; can be any fixed text, any existing replacement tag, or any &#8216;?&#8217; article variable so you can get really wild here, for example:</p>\n\n	<p><code>combo=&quot;imgin:found in ?s [tagged with ?keywords. Photo by {author}]&quot;</code></p>\n\n	<p>Once defined, <code>{imgin}</code> is available to your gallery <code>form</code> and might be replaced with &#8220;found in zoo [tagged with zebra, africa, stripes. Photo by Bloke]&#8221;. $pretext is also available for those geeks who know what&#8217;s in it (temporarily use <code>debug=&quot;4&quot;</code> to view all available article variables).</p>\n\n	<p>One thing to watch out for is that the space character isn&#8217;t necessarily a delimiter for <code>?</code> variables (because custom fields can have spaces in them). So <code>combo=&quot;doesnotwork:?s in ?category1&quot;</code> will look for a variable labelled &#8220;s in&#8221; and one called &#8220;category1&#8221;. Only one of those exists! Clever use of parentheses or other punctuation can work round this. Or use <code>strictfields=&quot;0&quot;</code> to turn off the use of spaces in custom field names.</p>\n\n	<h2 id="collate_mode">Collate mode</h2>\n\n	<p>If you want to get <em>really</em> funky you can put the plugin into a 2nd mode called &#8220;collation mode&#8221;. This means your chosen form is <strong>not</strong> executed for every image; it is only used once at the very end of the list, after all the images have been internally &#8220;collected&#8221;. In effect, the plugin builds up a giant array of all matching images ready for you to output them all in one go. This is very handy for doing slideshows and is best explained by example:</p>\n\n	<p><code>collate=&quot;1&quot;</code></p>\n\n	<p>switches collation mode on. If the plugin matched 4 images and you were to use the replacement tag <code>{name}</code> in your form like this:</p>\n\n<pre class="block"><code class="block">The matching images: {name}\n</code></pre>\n\n	<p>you might get this:</p>\n\n<pre class="block"><code class="block">The matching images: heron.jpg,penguin.jpg,emu.jpg,puffin.jpg\n</code></pre>\n\n	<p>Sometimes it&#8217;s useful to be able to put quotes around each item so you can tell collate mode to do that instead:</p>\n\n	<p><code>collate=&quot;quote:{name}&quot;</code></p>\n\n	<p>You would then get:</p>\n\n<pre class="block"><code class="block">The matching images: &quot;heron.jpg&quot;,&quot;penguin.jpg&quot;,&quot;emu.jpg&quot;,&quot;puffin.jpg&quot;\n</code></pre>\n\n	<p>The delimiter (a comma in this case) can be overridden with the <code>delim</code> attribute. You may use <code>sglquote</code> to wrap single quotes round the items, or &#8220;dblquote&#8221; as a synonym for &#8220;quote&#8221; if you prefer. You can quote more than one thing at once by specifying the items as a delimited list:</p>\n\n	<p><code>collate=&quot;quote:{name}:{alt}:{imgin}&quot;</code></p>\n\n	<p>Yes, you can use combos! The delimiter used between items can be overriden with the <code>collatedelim</code> attribute.</p>\n\n	<p>The second special feature of collation mode is that you do not have to always output the entire list. You can grab individual entities from within the internal array by using the &#8216;#&#8217; notation thus:</p>\n\n<pre class="block"><code class="block">{name} might output &quot;heron.jpg&quot;,&quot;penguin.jpg&quot;,&quot;emu.jpg&quot;,&quot;puffin.jpg&quot; (as before)\n{name#1} would only output heron.jpg\n{name#3} would only output emu.jpg\n</code></pre>\n\n	<p>Note that when pulling out individual entries they <strong>do not</strong> get quotes added to them, regardless of whether you used <code>quote</code> or not. This is because it is a single item so you can easily put the quotes in the form itself (viz: <code>&quot;{name#2}&quot;</code>)</p>\n\n	<p>Messing around with pulling out individual items and lists can yield some very interesting results and allows some completely wacky galleries to be generated. Try combining smd_gallery tags with <code>&lt;txp:if_different&gt;</code> for a real mind scramble :-)</p>\n\n	<h2 id="examples">Examples</h2>\n\n	<p>The examples all use the classic zoo website. Let&#8217;s get some pictures out of the database&#8230;</p>\n\n	<h3 id="eg1">Example 1: simple / lightboxed gallery</h3>\n\n<pre class="block"><code class="block">&lt;txp:smd_gallery category=&quot;animals&quot; sublevel=&quot;all&quot; form=&quot;gallery&quot; /&gt;\n</code></pre>\n\n	<p>That pulls out all pictures from the animals category, including all sub-categories, and passes them to the form called &#8216;gallery&#8217; for processing. The form could be:</p>\n\n<pre class="block"><code class="block">&lt;img class=&quot;animage&quot; src=&quot;{url}&quot; alt=&quot;{alt}&quot; /&gt;\n&lt;div class=&quot;acaption&quot;&gt;{title}&lt;/div&gt;\n</code></pre>\n\n	<p>That means, for every matched image, substitute its <span class="caps">URL</span>, alt and title tags into the relevant places. Thus you get a series of images on the page, which you can style accordingly.</p>\n\n	<p>For a more fancy output using, say, slimbox, you could load the relevant libraries and styles in the <code>&lt;head&gt;</code> of your page:</p>\n\n<pre class="block"><code class="block">&lt;script type=&quot;text/javascript&quot; src=&quot;/js/mootools.js&quot;&gt;&lt;/script&gt;\n&lt;script type=&quot;text/javascript&quot; src=&quot;/js/slimbox.js&quot;&gt;&lt;/script&gt;\n&lt;link rel=&quot;stylesheet&quot; type=&quot;text/css&quot; href=&quot;/css/slimbox.css&quot; /&gt;\n</code></pre>\n\n	<p>Then your &#8216;gallery&#8217; form could be:</p>\n\n<pre class="block"><code class="block">&lt;a rel=&quot;lightbox-{category}&quot; href=&quot;{url}&quot;\n     title=&quot;A picture of {caption}&quot;&gt;\n &lt;txp:thumbnail id=&quot;{id}&quot; /&gt;\n&lt;/a&gt;\n</code></pre>\n\n	<p>Voila! Using the same smd_gallery tag you have changed the way the gallery renders from a simple list to a full-featured slimbox. With virtually the same form you can switch the output to thickbox, lightbox, litebox, moonbox or whatever-you-like-box depending on which libraries you load. Follow the instructions for your favourite effect and plug it into smd_gallery.</p>\n\n	<h3 id="eg2">Example 2: launch from one image</h3>\n\n	<p>A common request is to be able to &#8220;launch&#8221; a slimbox gallery from a single image or text link. Using a single image:</p>\n\n<pre class="block"><code class="block">&lt;txp:smd_gallery category=&quot;animals&quot;\n     sublevel=&quot;all&quot;\n     form=&quot;gallery&quot; thumblimit=&quot;1&quot; /&gt;\n</code></pre>\n\n	<p>The form will use the special <code>{object}</code> replacement tag so it only displays the thumbnail if the value of thumblimit has <strong>not</strong> been reached:</p>\n\n<pre class="block"><code class="block">&lt;a rel=&quot;lightbox-smd&quot; href=&quot;{url}&quot;\n     title=&quot;A picture of {title}&quot;&gt;\n{object}\n&lt;/a&gt;\n</code></pre>\n\n	<p>Change the start image by specifying <code>thumboffset</code> &#8212; note this will also start the lightbox effect from the image you choose. If you want to start on a specific image in position 1, use the <code>id</code> attribute to bring it to the top of the list, or change the sort order.</p>\n\n	<p>If you wanted to launch the gallery from a text link, add something like <code>text=&quot;Click to start lightbox&quot;</code>.</p>\n\n	<h3 id="eg3">Example 3: popup gallery</h3>\n\n	<p>To achieve an effect similar to rss_thumbpop, add an image and caption placeholder outside the call to smd_gallery, then include your smd_gallery tag as usual. This time we are only interested in all the pictures from the categories up to two levels below both &#8220;animals&#8221;, plus whichever categories are listed in <code>my_custom_field</code> and the images with IDs 25, 38, 39, 40 and 42:</p>\n\n<pre class="block"><code class="block">&lt;img id=&quot;fullsize&quot; src=&quot;/images/empty.jpg&quot; /&gt;\n&lt;span id=&quot;fullcaption&quot;&gt;&lt;/span&gt;\n&lt;txp:smd_gallery sublevel=&quot;2&quot;\n     category=&quot;animals, ?my_custom_field&quot;\n     id=&quot;25, 42, 38-40&quot; form=&quot;gallery&quot; /&gt;\n</code></pre>\n\n	<p>The gallery form looks scary but is only a thumbnail image surrounded by a simple anchor tag with two actions assigned to it: a mouseover to copy the picture and caption into the placeholder; and a mouseout to remove them:</p>\n\n<pre class="block"><code class="block">&lt;a href=&quot;{url}&quot; title=&quot;{title}&quot;\n     onmouseover=&quot;document.getElementById(&#39;fullsize&#39;).src = this.href;\n     document.getElementById(&#39;fullcaption&#39;).innerHTML = this.title; return false;&quot;\n     onmouseout=&quot;document.getElementById(&#39;fullsize&#39;).src = &#39;/images/17.jpg&#39;;\n     document.getElementById(&#39;fullcaption&#39;).innerHTML = &#39;&#39;; return false;&quot;&gt;\n  &lt;img src=&quot;{thumburl}&quot; alt=&quot;{alt}&quot; /&gt;\n&lt;/a&gt;\n</code></pre>\n\n	<p>Note there are much neater ways of doing this (e.g. with jQuery) and it&#8217;s not recommended to use this form in a live site, but it gives a flavour of what is possible.</p>\n\n	<h3 id="eg4">Example 4: slideshow</h3>\n\n	<p>This one uses collate mode but is not much more difficult to understand. In your <code>&lt;head&gt;</code> section:</p>\n\n<pre class="block"><code class="block">&lt;script type=&quot;text/javascript&quot; src=&quot;/js/mootools.js&quot;&gt;&lt;/script&gt;\n&lt;script type=&quot;text/javascript&quot; src=&quot;/js/slideshow.js&quot;&gt;&lt;/script&gt;\n</code></pre>\n\n	<p>The tag:</p>\n\n<pre class="block"><code class="block">&lt;txp:smd_gallery\n  category=&quot;birds, fish&quot; form=&quot;gallery&quot;\n  combo=&quot;imglist:{id}{ext}&quot;\n  collate=&quot;quote:{imglist}:{alt}&quot; /&gt;\n</code></pre>\n\n	<p>That pulls all pictures from the birds and fish categories. Although technically unnecessary, a combo has been defined to show how it can be used. This combo is called <code>imglist</code> and is defined as the image&#8217;s ID number plus its extension. We use this in the collate attribute and tell it to put double quotes round every item in &#8216;imglist&#8217; and &#8216;alt&#8217;. Then we can put these values into the necessary javascript in the <code>gallery</code> form to generate the slideshow:</p>\n\n<pre class="block"><code class="block">&lt;div class=&quot;slideshow&quot; id=&quot;slideshow1&quot;&gt;\n &lt;img src=&quot;&lt;txp:site_url /&gt;images/{id#1}.jpg&quot;\n     alt=&quot;{alt#1}&quot; width=&quot;{width#1}&quot; height=&quot;{height#1}&quot; /&gt;\n&lt;/div&gt;\n</code>\n<code class="block">&lt;script type=&quot;text/javascript&quot;&gt;\nmyShow1 = new Slideshow(&quot;slideshow1&quot;,\n  { hu: &quot;&lt;txp:site_url /&gt;images/&quot;,\n    images: [{imglist}],\n    captions: [{alt}],\n    classes: [&quot;prev&quot;,&quot;next&quot;,&quot;active&quot;],\n    type: &quot;fade&quot;\n  });\n&lt;/script&gt;\n</code></pre>\n\n	<p>Note that the initial portion (in the div and img tags) uses the &#8216;#&#8217; notation because we only want to display one image to &#8220;start&#8221; the slideshow. In this case we&#8217;re using the first image in the list, but you can change the #1 for #42 if you wanted to show the forty-second image instead! The part in the script tag uses the full, quoted lists because that&#8217;s the format the javascript requires.</p>\n\n	<h3 id="eg5">Example 5: pagination</h3>\n\n	<p>To add paging to a gallery you use the <code>limit</code> attribute to show a number of thumbs per page. For a paged thumbpop gallery that shows five thumbs per page:</p>\n\n<pre class="block"><code class="block">&lt;img id=&quot;fullsize&quot; src=&quot;/images/empty.jpg&quot; /&gt;\n&lt;span id=&quot;fullcaption&quot;&gt;&lt;/span&gt;\n&lt;txp:smd_gallery category=&quot;animals&quot;\n  form=&quot;gallery&quot; pageform=&quot;gallery_nav&quot;\n  limit=&quot;5&quot; /&gt;\n</code></pre>\n\n	<p>The &#8216;gallery&#8217; form is identical to the one in <a href="#eg4">example 4</a>, while your gallery_nav form might look like this:</p>\n\n<pre class="block"><code class="block">&lt;div class=&quot;total&quot;&gt;# of pics: {totalimages}&lt;/div&gt;\n     {navfirst} {navprev}\n     {navearlier} {navthis} {navlater}\n     {navnext} {navlast}\n</code></pre>\n\n	<p>That might render something similar to this below the thumbs:</p>\n\n<pre class="block"><code class="block"># of pics: 38\n     First &lt; 1 2 3 4 5 6 7 8 &gt; Last\n</code></pre>\n\n	<h3 id="eg6">Example 6: onchange</h3>\n\n	<p>If you want to simulate multiple galleries from one smd_gallery tag you can use <code>onchange</code> to &#8220;watch&#8221; for when certain attributes of images change and insert special tags <em>only when something changes</em>. In this regard it acts like a souped-up version of <span class="caps">TXP</span>&#8217;s built-in <code>&lt;txp:if_different&gt;</code> tag.</p>\n\n	<p>You can watch the usual things like <code>category</code>, <code>author</code>, <code>width</code>, <code>height</code>, <code>ext</code>ension and so on:</p>\n\n<pre class="block"><code class="block">&lt;txp:smd_gallery\n     category=&quot;birds, fish&quot; form=&quot;gallery&quot;\n     onchange=&quot;category_title&quot;\n     onchangewraptag=&quot;h3&quot; /&gt;\n</code></pre>\n\n	<p>Since the plugin normally sorts by category, this will have the effect of adding one replacement tag <code>{onchange:category_title}</code> at the very beginning of the thumb list and also when the category switches from &#8216;birds&#8217; to &#8216;fish&#8217;. Thus your gallery form could be:</p>\n\n<pre class="block"><code class="block">{onchange:category_title}\n&lt;a href=&quot;{url}&quot; rel=&quot;lightbox-{category}&quot;&gt;\n{object}\n&lt;/a&gt;\n</code></pre>\n\n	<p>That would make two lightboxable sets of thumbnails with a heading 3 above each group containing the category title.</p>\n\n	<p>Note that if you add <code>thumblimit=&quot;1&quot;</code> to the smd_gallery tag you can show just one thumbnail in each group, with headings above each thumb. Visitors clicking the thumb will launch the lightbox and be able to browse the relevant category. If you want to make sure that they can only browse six images in each category set, add <code>grouplimit=&quot;6&quot;</code>.</p>\n\n	<p>An extra pair of replacement tags are available if you wish to wrap the objects themselves to group them. Use the <code>grouptag</code> and <code>groupclass</code> attributes to set it up, then two new replacement tags called <code>grouptagstart</code> and <code>grouptagend</code> become available. They are conditional and are only populated when the attributes you are watching/sorting by change. Use it like this:</p>\n\n<pre class="block"><code class="block">&lt;txp:smd_gallery id=&quot;?article_image&quot;\n     category=&quot;birds, fish&quot; form=&quot;gallery&quot;\n     onchange=&quot;category_title&quot;\n     onchangewraptag=&quot;h3&quot;\n     grouptag=&quot;div&quot; groupclass=&quot;box&quot; /&gt;\n</code></pre>\n\n<pre class="block"><code class="block">{onchange:category_title}\n{grouptagstart}\n&lt;a href=&quot;{url}&quot; rel=&quot;lightbox-{category}&quot;&gt;\n{object}\n&lt;/a&gt;\n{grouptagend}\n</code></pre>\n\n	<p>Note that you can move the <code>grouptagstart</code> to surround the <code>onchange</code>d title if you wish.</p>\n\n	<h3 id="eg7">Example 7: smd_every</h3>\n\n	<p>You can also trigger an onchange event every nth image:</p>\n\n<pre class="block"><code class="block">&lt;txp:smd_gallery category=&quot;frontpage&quot;\n     limit=&quot;9&quot; sort=&quot;random&quot;\n     onchange=&quot;smd_every:3&quot;\n     grouptag=&quot;ul&quot; groupclass=&quot;slideshow&quot;&gt;\n{grouptagstart}\n   &lt;li&gt;\n      &lt;a href=&quot;&lt;txp:permlink id=&quot;{alt}&quot; /&gt;&quot;&gt;\n         &lt;img src=&quot;{thumburl}&quot; alt=&quot;&quot; /&gt;\n         &lt;span&gt;{title}&lt;/span&gt;\n      &lt;/a&gt;\n   &lt;/li&gt;\n{grouptagend}\n&lt;/txp:smd_gallery&gt;\n</code></pre>\n\n	<h3 id="eg8">Example 8: uncategorized images</h3>\n\n	<p>Sometimes you might wish to extract images that do not have a category assigned. You need to use the special value <code>SMD_EMPTY</code> for this:</p>\n\n<pre class="block"><code class="block">&lt;txp:smd_gallery category=&quot;SMD_EMPTY, shoes&quot; /&gt;\n</code></pre>\n\n	<p>You can also negate the logic so you can extract all images that have some (any) category assigned to them:</p>\n\n<pre class="block"><code class="block">&lt;txp:smd_gallery category=&quot;!SMD_EMPTY&quot; /&gt;\n</code></pre>\n\n	<h3 id="eg9">Example 9: {object} and objectform</h3>\n\n	<p>The default <code>{object}</code> outputs a simple hyperlinked thumbnail. If you want something a bit more exotic, like launching a shadowbox effect from a single thumbnail that also has the caption beneath it, you need to customise the <code>{object}</code> via the <code>objectform</code> attribute. Put a list of image IDs in your article image field and then:</p>\n\n<pre class="block"><code class="block">&lt;txp:smd_gallery thumblimit=&quot;1&quot;\n     id=&quot;?article_image&quot; wraptag=&quot;div&quot;\n     class=&quot;thumbs&quot; objectform=&quot;my_obj&quot;\n     combo=&quot;artid:?thisid&quot;&gt;\n  &lt;a rel=&quot;shadowbox[{artid}];\n     options={counterType:&#39;skip&#39;,\n     continuous:true,\n     animate:false,\n     animSequence:&#39;sync&#39;}&quot;\n     href=&quot;{url}&quot;\n     title=&quot;{alt}&quot;&gt;{object}&lt;/a&gt;\n&lt;/txp:smd_gallery&gt;\n</code></pre>\n\n	<p>And in <code>my_obj</code>, you could put:</p>\n\n<pre class="block"><code class="block">&lt;txp:thumbnail id=&quot;{id}&quot; /&gt;&lt;br /&gt;{title}\n</code></pre>\n\n	<p>Notice how the replacement tags still work in the <code>objectform</code>? This allows you to build up a complete layout for the launchable thumbnail and have it only rendered once on the page. The rest of the container in this example renders all the &#8220;hidden&#8221; images in the shadowbox gallery.</p>\n\n	<h3 id="eg10">Example 10: <code>countform</code></h3>\n\n<pre class="block"><code class="block">&lt;txp:smd_gallery category=&quot;animals&quot;\n     countform=&quot;1:gal_a:smd_above,\n       {totalimages}:gal_c,\n       smd_every:3:!{totalimages}:gal_b&quot;\n     countpos=&quot;below&quot;&gt;\n&lt;txp:thumbnail id=&quot;{id}&quot; /&gt;\n&lt;/txp:smd_gallery&gt;\n</code></pre>\n\n	<p>Note that <code>countpos</code> is set to <code>below</code> in this example so all output appears <em>after</em> the given images. If you made the following three forms, your tag would split the gallery into &#8216;rows&#8217; of 3 images:</p>\n\n	<p>gal_a: <code>&lt;div class=&quot;newrow&quot;&gt;</code><br />\ngal_b: <code>&lt;/div&gt;&lt;div class=&quot;newrow&quot;&gt;</code><br />\ngal_c: <code>&lt;/div&gt;</code></p>\n\n	<p>Breaking the <code>countform</code> attribute down into its components, you can see that:</p>\n\n	<ul>\n		<li><code>1:gal_a:smd_above</code> adds the &#8216;newrow&#8217; div above the first image</li>\n		<li><code>{totalimages}:gal_c</code> adds the closing div below the last image on this page</li>\n		<li><code>smd_every:3:!{totalimages}:gal_b</code> adds a closing and then an opening div after every 3rd image, <em>except</em> on the last image. If we didn&#8217;t put the exception in, and you had 12 thumbnails in your gallery (which is divisible by 3!) you would see the contents of gal2 after the last image as well</li>\n	</ul>\n\n	<h3 id="eg11">Example 11: Using an onchange form</h3>\n\n<pre class="block"><code class="block">&lt;txp:smd_gallery\n     category=&quot;birds, fish&quot;\n     onchange=&quot;category_title:oc_form&quot;&gt;\n{onchange:category_title}\n&lt;a href=&quot;{url}&quot; rel=&quot;lightbox-{category}&quot;&gt;\n{object}\n&lt;/a&gt;\n&lt;/txp:smd_gallery&gt;\n</code></pre>\n\n	<p>and in form <code>oc_form</code>:</p>\n\n<pre class="block"><code class="block">&lt;div id=&quot;{category}&quot;\n   class=&quot;category&quot;&gt;{category_title}&lt;/div&gt;\n</code></pre>\n\n	<h3 id="eg12">Example 12: Portfolio landing page</h3>\n\n	<p>Here we will render a gallery for each artist on a gallery site. Arrange your image categories so they are children of a parent image category:</p>\n\n<pre class="block"><code class="block">portfolio\n  |--&gt; monet\n  |--&gt; van_gogh\n  |--&gt; turner\n  |--&gt; dali\n  |--&gt; ...\n</code></pre>\n\n	<p>Then of course, upload your images and assign them to the appropriate artist category.</p>\n\n<pre class="block"><code class="block">&lt;txp:smd_gallery\n     category=&quot;portfolio&quot; sublevel=&quot;1&quot;\n     onchange=&quot;category_title&quot;\n     onchangewraptag=&quot;h3&quot; thumblimit=&quot;1&quot;\n     grouplimit=&quot;5&quot; sort=&quot;category asc, date desc&quot;\n     objectform=&quot;full_link&quot;&gt;\n{onchange:category_title}\n&lt;a href=&quot;{url}&quot; rel=&quot;lightbox-{category}&quot;&gt;\n{object}\n&lt;/a&gt;\n&lt;/txp:smd_gallery&gt;\n</code></pre>\n\n	<p>In Form <code>full_link</code>:</p>\n\n<pre class="block"><code class="block">&lt;txp:thumbnail id=&quot;{id}&quot; /&gt;\n&lt;span&gt;\n   See &lt;a href=&quot;/artists/{category}&quot;&gt;all images by {category_title}&lt;/a&gt;.\n&lt;/span&gt;\n</code></pre>\n\n	<p>That will grab each artist category and display a thumbnail of the most recent work submitted by each author under their own heading. When you click the thumbnail it launches the lightbox gallery and allows you to navigate through the five most recent pictures submitted. Beneath the thumbnail is a link to an individual article that could show that artists&#8217;s complete works and a bio.</p>\n\n	<h2 id="changelog">Changelog</h2>\n\n	<p>All available versions and changes are listed here.</p>\n\n	<p><strong>Beta</strong></p>\n\n	<ul>\n		<li>14 Feb 08 | 0.1 | Initial release &#8211; Happy Valentines Day :-)</li>\n		<li>19 Feb 08 | 0.2 | Added {galleryid} (thanks rloaderro) and some skeleton docs</li>\n	</ul>\n\n	<p><strong>Official</strong></p>\n\n	<ul>\n		<li>11 Mar 08 | 0.3 | Official public release. Fixed silly empty field bug; added {counter}, {index} and the totals (all thanks jakob)</li>\n		<li>15 Mar 08 | 0.31 | Fixed a couple of undefined indexes and added {category_title} (thanks jstubbs)</li>\n		<li>17 Mar 08 | 0.32 | Fixed another undefined index and removed bogus call to smd_lib (both thanks secundar)</li>\n		<li>21 Mar 08 | 0.33 | Did v0.31 properly, including table prefix (thanks nardo)</li>\n		<li>01 Apr 08 | 0.34 | Fixed <span class="caps">SQL</span> parenthesis bug ; added emptyclass and {maximages} (thanks the_ghost)</li>\n		<li>08 Apr 08 | 0.4 | Changed thumb to allow thumb/fullsize swapping ; Added thumbchange, thumbchangewraptag, thumbchangeclass, {thumbindex}, {thumbcounter} and the {changed:*} replacement tags (thanks fuls)</li>\n		<li>26 May 08 | 0.41 | Fixed Directory separator (thanks cuda) ; added raw page <span class="caps">URL</span>s to the nav replacement array and made the nav array available in the standard replacements array (thanks visualpeople)</li>\n		<li>27 May 08 | 0.42 | Added pagepos and made page blocks independent of thumb block (thanks duchamp)</li>\n		<li>10 Jun 08 | 0.43 | Fixed sort=&#8220;fixed&#8221; when using limit (thanks robin746) ; added per-group onchange wrapping and allowed fixed sorting of categories (thanks jstubbs) ; deprecated thumbchange* and renamed as onchange* ; fixed $idx counter problem</li>\n		<li>10 Jun 08 | 0.44 | Attempt #2 at per-group onchange wrapping (thanks again jstubbs)</li>\n		<li>14 Jul 08 | 0.45 | Fixed lack of output when not using categories (thanks robin746)</li>\n		<li>16 Nov 08 | 0.46 | Added collatedelim (thanks curiouz) ; fixed (I hope) the category / id bug from 0.44 and 0.45 (thanks robin746/nardo)</li>\n		<li>13 Feb 09 | 0.5 | Requires smd_lib ; added <span class="caps">MLP</span> support (thanks net-carver) ; added support for displaying uncategorised images (thanks xzamen) ; &#8216;?&#8217; vars expanded to link / image / file / <code>&lt;txp:variable /&gt;</code> and <span class="caps">URL</span> variable ; new attributes: <code>escape</code>, <code>html_id</code>, <code>format</code>, <code>gmt</code>, <code>lang</code> (thanks maniqui), <code>strictfields</code> ; improved <code>{object}</code> support and added <code>objectform</code> (thanks tye/rvturnage) ; <code>{title}</code> == <code>{caption}</code> at last! ; removed <code>thumbchange</code>* attributes ; improved support for table extensions (via the <code>columns</code> attribute: thanks RedFox) ; added <code>smd_every</code> syntax to <code>onchange</code> (thanks jakob) ; fixed offset bug (thanks feragnoli)</li>\n		<li>01 Mar 09 | 0.51 | Added <code>countform</code> and <code>countpos</code> ; fixed <code>{hasthumb}</code> warning</li>\n		<li>16 Nov 09 | 0.52 | <span class="caps">TXP</span> 4.2.0 only ; added database thumbnail height and width support ; added <code>aspect</code>, <code>dimensions</code>, <code>author</code>, <code>extension</code> and <code>imgsize</code> attributes ; fixed quote parsing in <code>escape</code> attribute and warning when using <code>fixed</code> sorting (both thanks maniqui)</li>\n		<li>01 Jan 10 | 0.53 | Added onchange form capability (thanks MattD)</li>\n		<li>01 Feb 10 | 0.54 | Multiple images not displayed if multiple categories exist with the same name and different title</li>\n		<li>01 Mar 10 | 0.55 | Added <code>name</code> filtering</li>\n		<li>10 Mar 10 | 0.6 | Simplified query to eliminate errors with uncategorised images being omitted from the results; allowed countform ranges ; added <code>grouplimit</code></li>\n		<li>03 Apr 10 | 0.61 | Added <code>breakclass</code> (thanks the_ghost)</li>\n		<li>06 Apr 10 | 0.62 | Fixed <code>{onchange:category_title}</code> and <code>{grouptagend}</code> (thanks jan)</li>\n	</ul>\n\n	<h2 id="credits">Credits</h2>\n\n	<p>This plugin wouldn&#8217;t have existed without the awesome mind of Robert Wetzlmayr. His wet_for_each_image plugin is true genius; this simply stands on the shoulders of that idea and rolls in a souped up version of the thumbnail generation engine from smd_slimbox. To that end, the plugin is also indebted to anyone who has posted features or bug reports to me or the smd_slimbox thread. You&#8217;re all marvellous.</p>\n\n</div>\n<!-- *** BEGIN PLUGIN CSS *** -->\n<style type="text/css">\n#smd_help { line-height:1.5 ;}\n#smd_help code { font-weight:bold; font: 105%/130% "Courier New", courier, monospace; background-color: #FFFFCC;}\n#smd_help code.block { font-weight:normal; border:1px dotted #999; background-color: #f0e68c; display:block; margin:10px 10px 20px; padding:10px; }\n#smd_help a:link, #smd_help a:visited { color: #00c; text-decoration: none; border-bottom: 1px solid blue; padding-bottom:1px;}\n#smd_help a:hover, #smd_help a:active { color: blue; text-decoration: none; border-bottom: 2px solid blue; padding-bottom:1px;}\n#smd_help h1 { color: #369; font: 20px Georgia, sans-serif; margin: 0; text-align: center; }\n#smd_help h2 { border-bottom: 1px solid black; padding:10px 0 0; color: #369; font: 17px Georgia, sans-serif; }\n#smd_help h3 { color: #079; font: bold 12px Arial, sans-serif; letter-spacing: 1px; margin: 10px 0 0;text-transform: uppercase; text-decoration:underline;}\n#smd_help h4 { font: bold 11px Arial, sans-serif; letter-spacing: 1px; margin: 10px 0 0 ;text-transform: uppercase; }\n#smd_help .atnm { font-weight:bold; color:#33d; }\n#smd_help .mand { background:#eee; border:1px dotted #999; }\n#smd_help table {width:90%; text-align:center; padding-bottom:1em;}\n#smd_help td, #smd_help th {border:1px solid #999; padding:.5em 0;}\n#smd_help ul { list-style-type:square; }\n#smd_help .required {color:red;}\n#smd_help li { margin:5px 20px 5px 30px; }\n#smd_help .break { margin-top:5px; }\n</style>\n<!-- *** END PLUGIN CSS *** -->\n', 'require_plugin(''smd_lib'');\r\n\r\n// MLP support\r\nglobal $smd_gallLang;\r\n$smd_gallStr = array(\r\n	''navfirst'' => ''First'',\r\n	''navlast'' => ''Last'',\r\n	''navprev'' => ''&laquo;'',\r\n	''navnext'' => ''&raquo;'',\r\n);\r\n$smd_gallLang = new smd_MLP(''smd_gallery'', ''smd_gall'', $smd_gallStr);\r\n\r\nfunction smd_gallery($atts, $thing='''') {\r\n	global $pretext, $thisarticle, $img_dir, $dateformat, $smd_gallLang, $l10n_language;\r\n\r\n	// Plugin options\r\n	extract(lAtts(array(\r\n		''columns'' => '''',\r\n		''delim'' => '','',\r\n		''paramdelim'' => '':'',\r\n		''collatedelim'' => '','',\r\n		''strictfields'' => ''0'',\r\n		''form'' => '''',\r\n		''countform'' => '''',\r\n		''countpos'' => ''above'',\r\n		''id'' => '''',\r\n		''name'' => '''',\r\n		''category'' => '''',\r\n		''aspect'' => '''',\r\n		''author'' => '''',\r\n		''dimensions'' => '''',\r\n		''extension'' => '''',\r\n		''directory'' => '''',\r\n		''match'' => '''',\r\n		''sublevel'' => ''0'',\r\n		''escape'' => ''alt, title, caption'',\r\n		''sort'' => '''',\r\n		''offset'' => ''0'',\r\n		''limit'' => ''0'',\r\n		''maxlimit'' => ''99999'',\r\n		''grouplimit'' => ''99999'',\r\n		''thumblimit'' => ''99999'',\r\n		''thumboffset'' => ''0'',\r\n		''thumb'' => ''0:1'',\r\n		''thumbsize'' => '''',\r\n		''imgsize'' => '''',\r\n		''onchange'' => ''category'',\r\n		''onchangewraptag'' => '''',\r\n		''onchangeclass'' => '''',\r\n		''objectform'' => '''',\r\n		''grouptag'' => '''',\r\n		''groupclass'' => '''',\r\n		''text'' => '''',\r\n		''format'' => $dateformat,\r\n		''gmt'' => '''',\r\n		''lang'' => '''',\r\n		''collate'' => '''',\r\n		''combo'' => '''',\r\n		''paging'' => ''0'',\r\n		''pageform'' => '''',\r\n		''pagepos'' => ''below'',\r\n		''navwraptag'' => ''ul'',\r\n		''navbreak'' => ''li'',\r\n		''navclass'' => ''smd_gallery_nav'',\r\n		''emptyclass'' => ''empty'',\r\n		''galleryid'' => ''?4:6'',\r\n		''wraptag'' => '''',\r\n		''html_id'' => '''',\r\n		''break'' => '''',\r\n		''class'' => '''',\r\n		''breakclass'' => '''',\r\n		''debug'' => ''0'',\r\n	), $atts));\r\n\r\n	// Category relies on whether id has been specified or not.\r\n	// lAtts() won''t let us handle this logic so it has to be done manually\r\n	$attemptMatch = (empty($category) && empty($id) && empty($name)) ? false : true;\r\n	$category = isset($category) ? doSlash($category) : (($id=='''') ? $pretext[''c'']: '''');\r\n	$sort = ($sort) ? $sort : ( ($id) ? ''id'' : ''category'' );\r\n\r\n	// ** LOOKUP TABLES\r\n	// $fieldMap holds database column as key, and replacement identifier as value.\r\n	// Additional cols copied to $extraMap for iteration while creating replacements.\r\n	// Use array_search to find if value exists and return correct key for use in db calls/array refs.\r\n	$fieldMap = array();\r\n	$extraMap = array();\r\n	$coreCols = array("name", "id", "alt", "caption", "category", "author", "date", "ext", "w:width", "h:height", "thumb_w:thumb_width", "thumb_h:thumb_height", "thumbnail:hasthumb");\r\n	$mlpFields = array("alt", "caption"); // Manually create the MLP column names, since using getRows() later does not. Category title also has the same treatment\r\n	$thislang = (isset($l10n_language[''long''])) ? $l10n_language[''long''] : LANG;\r\n	$dflt_tbl = PFX."txp_image";\r\n	$columns = do_list($columns);\r\n	foreach (array_merge($coreCols, $columns) as $colinfo) {\r\n		$colbit = do_list($colinfo, ":");\r\n		$coltbl = do_list($colbit[0], ".");\r\n\r\n		if ($colbit[0] == '''') continue;\r\n\r\n		if (count($coltbl) > 1) {\r\n			$tbl = $coltbl[0];\r\n			$colname = $coltbl[1];\r\n		} else {\r\n			$tbl = $dflt_tbl;\r\n			$colname = $coltbl[0];\r\n		}\r\n		$colid = (count($colbit) > 1) ? $colbit[1] : $colname;\r\n		$fieldMap[(($tbl)?$tbl.''.'':'''').( (isset($l10n_language[''long'']) && in_array($colname, $mlpFields)) ? _l10n_make_field_name($colname, $thislang). '' AS ''.$colname  : $colname )] = $colid;\r\n		if (!in_array($colinfo, $coreCols)) {\r\n			$extraMap[$colname] = $colid;\r\n		}\r\n	}\r\n	$fieldMap = array_unique($fieldMap);\r\n\r\n	// Handle forms and set a default tag if none specified\r\n	$thing = (empty($form)) ? $thing : fetch_form($form);\r\n	$thing = (empty($thing)) ? ''<a href="{url}" alt="{alt}"><txp:image id="{id}" /></a>'' : $thing;\r\n	$objectform = (empty($objectform)) ? '''' : fetch_form($objectform);\r\n\r\n	// Counting\r\n	$countPosAllowed = array("above", "below");\r\n	$countposns = array();\r\n	$counting = 0;\r\n	if ($countform) {\r\n		$counting = 1;\r\n		$countform = do_list($countform, $delim);\r\n		$countpos = str_replace(''smd_'', '''', $countpos); // For convenience\r\n		$countpos = do_list($countpos, $delim);\r\n		foreach ($countpos as $countitem) {\r\n			$countposns[] = (in_array($countitem, $countPosAllowed)) ? $countitem : $countPosAllowed[0];\r\n		}\r\n	}\r\n\r\n	// Paging init\r\n	$pagePosAllowed = array("below", "above");\r\n	$pageout = array();\r\n	if ($pageform) {\r\n		$paging = 1;\r\n		$pageform = fetch_form($pageform);\r\n		$pagepos = str_replace(''smd_'', '''', $pagepos);\r\n		$pagepos = do_list($pagepos, $delim);\r\n		foreach ($pagepos as $pageitem) {\r\n			$pageout[] = (in_array($pageitem, $pagePosAllowed)) ? $pageitem : $pagePosAllowed[0];\r\n		}\r\n	}\r\n\r\n	// onchange verification\r\n	$onchange = do_list($onchange, $delim);\r\n	$onchange = ($onchange[0] == "") ? array() : $onchange;\r\n	$resetOpts = $resetForms = array();\r\n	foreach ($onchange as $resetOpt) {\r\n		$resetParts = do_list($resetOpt, $paramdelim);\r\n\r\n		if (count($resetParts) > 1) {\r\n			if ($resetParts[0] == ''smd_every'') {\r\n				$resetOpts[$resetParts[0].$paramdelim.$resetParts[1]] = "";\r\n			} else {\r\n				$resetForms[$resetParts[0]] = $resetParts[1];\r\n				$resetOpts[$resetParts[0]] = "";\r\n			}\r\n		} else {\r\n			$resetOpts[$resetParts[0]] = "";\r\n		}\r\n	}\r\n\r\n	// Escaping\r\n	$spc = ($strictfields) ? 0 : 1;\r\n	$escapes = do_list($escape, $delim);\r\n	foreach ($escapes as $idx => $val) {\r\n		if ($val == '''') {\r\n			unset($escapes[$idx]);\r\n		}\r\n	}\r\n\r\n	// The gallery identifier is used for keeping track of next/prev thumbnail page links\r\n	$gallID = '''';\r\n	if ($galleryid == '''' || substr($galleryid,0,1) == "?") {\r\n		// Generate a (fairly likely) unique ID for this gallery instance\r\n		$gallTmp = md5($category.$id.$directory.$match.$sublevel.$sort.$limit.$maxlimit);\r\n		list($gallLen, $gallSkip) = explode(":", substr($galleryid,1));\r\n		$gallLen =  (empty($gallLen)) ? "4" : $gallLen;\r\n		$gallSkip =  (empty($gallSkip)) ? "1" : $gallSkip;\r\n		for ($idx = 0, $cnt = 0; $cnt < $gallLen; $cnt++, $idx = (($idx+$gallSkip) % strlen($gallTmp))) {\r\n			$gallID .= $gallTmp[$idx];\r\n		}\r\n	} else {\r\n		$gallID = $galleryid;\r\n	}\r\n	$gallID = "smd_glry_".$gallID; // Avoid (already incredibly unlikely) namespace clashes\r\n\r\n	// Set up the variables from the thumb option.\r\n	// Don''t use paramdelim here or the default option breaks\r\n	$thumbOpts = do_list($thumb, ":");\r\n	if (count($thumbOpts) == 1) {\r\n		$fullmod = ($thumbOpts[0] == 1) ? "t" : "";\r\n		$thumbmod = "t";\r\n	} else {\r\n		$fullmod = ($thumbOpts[0] == 1) ? "t" : "";\r\n		$thumbmod = ($thumbOpts[1] == 1) ? "t" : "";\r\n	}\r\n\r\n	// The manner of obtaining files differs if using directory vs category/id\r\n	$rs = array();\r\n	if ($directory) {\r\n		$rs = smd_doDirectory($directory, 0, $sublevel, $match, $delim, $spc);\r\n		list($allImages, $numPages, $thumPage, $offset) = smd_getPage($rs, $gallID, $limit, $offset, $paging);\r\n		$dirlimit = ($limit==0) ? $maxlimit : $limit;\r\n		$postOrder = array();\r\n		$fixedOrder = 0;\r\n		$randsort = false;\r\n	} else {\r\n		// Process categories/IDs and make up the crux of the where clause.\r\n		// Clause order = IDs OR cats AND !cats AND !IDs\r\n		$clause[] = ''1=1'';\r\n		$clauseOR = array();\r\n\r\n		// Surround each entry in the ID list with quotes and make an SQL "in" list\r\n		$subinfo = ($sublevel) ? "image:".$sublevel : '''';\r\n		list($idinc, $idexc) = smd_doList($id, true, '''', true, $delim, $spc);\r\n		list($naminc, $namexc) = smd_doList($name, false, '''', false, $delim, $spc);\r\n		list($catinc, $catexc) = smd_doList($category, false, $subinfo, true, $delim, $spc);\r\n		if ($idinc) {\r\n			$clauseOR[] = PFX.''txp_image.id IN (''.implode(",", $idinc).'')'';\r\n		}\r\n		if ($naminc) {\r\n			$namlist = array(''regex'' => array(), ''exact'' => array());\r\n			foreach ($naminc as $theName) {\r\n				$namebits = do_list($theName, $paramdelim);\r\n				$namebits[1] = (isset($namebits[1]) && array_key_exists($namebits[1], $namlist)) ? $namebits[1] : ''exact'';\r\n				$namlist[$namebits[1]][] = $namebits[0];\r\n			}\r\n			if ($namlist[''exact'']) {\r\n				$clauseOR[] = PFX."txp_image.name IN (''".implode("'',''", $namlist[''exact''])."'')";\r\n			}\r\n			foreach ($namlist[''regex''] as $re) {\r\n				$clauseOR[] = PFX."txp_image.name REGEXP ''".preg_quote($re)."''";\r\n			}\r\n		}\r\n		if ($catinc) {\r\n			$clauseOR[] = PFX.''txp_image.category IN (''.implode(",", $catinc).'')'';\r\n		}\r\n		if (!empty($clauseOR)) {\r\n			$clause[] = '' (''.implode(" OR ", $clauseOR).'') '';\r\n		}\r\n\r\n		$clause = implode(" AND ", $clause);\r\n		$clause .= ($catexc) ? '' AND ''.PFX.''txp_image.category NOT IN (''.implode(",", $catexc).'')'' : '''';\r\n		$clause .= ($idexc) ? '' AND ''.PFX.''txp_image.id NOT IN (''.implode(",", $idexc).'')'' : '''';\r\n\r\n		if ($namexc) {\r\n			$namlist = array(''regex'' => array(), ''exact'' => array());\r\n			foreach ($namexc as $theName) {\r\n				$namebits = do_list($theName, $paramdelim);\r\n				$namebits[1] = (isset($namebits[1]) && array_key_exists($namebits[1], $namlist)) ? $namebits[1] : ''exact'';\r\n				$namlist[$namebits[1]][] = $namebits[0];\r\n			}\r\n			if ($namlist[''exact'']) {\r\n				$clause .= " AND ".PFX."txp_image.name NOT IN (''".implode("'',''", $namlist[''exact''])."'')";\r\n			}\r\n			foreach ($namlist[''regex''] as $re) {\r\n				$clause .= " AND ".PFX."txp_image.name NOT REGEXP ''".preg_quote($re)."''";\r\n			}\r\n		}\r\n\r\n		// If a category/id option was chosen and they matched nothing, use a bogus category that should not exist.\r\n		// This ensures that the plugin does the expected thing of matching nothing in the event it finds nothing.\r\n		if ($clause == "1=1" && $attemptMatch) {\r\n	  		$clause = PFX.''txp_image.category = "smd_'' .mt_rand(). ''"'';\r\n		}\r\n\r\n		$clause = ''(''.str_replace(''SMD_EMPTY'', '''', $clause).'')'';\r\n\r\n		if ($debug > 1) {\r\n			echo "++ INCLUDED/EXCLUDED IDs, NAMEs AND CATs ++";\r\n			if ($idinc) dmp($idinc);\r\n			if ($idexc) dmp($idexc);\r\n			if ($naminc) dmp($naminc);\r\n			if ($namexc) dmp($namexc);\r\n			if ($catinc) dmp($catinc);\r\n			if ($catexc) dmp($catexc);\r\n		}\r\n\r\n		// Filter by aspect ratio\r\n		if ($aspect) {\r\n			$aspects = do_list($aspect, $delim);\r\n\r\n			// Compare fullsize or thumbnail aspect ratios\r\n			$fldmod = ($fullmod) == ''t'' ? ''thumb_'' : '''';\r\n			$wfld = PFX.''txp_image.''.$fldmod.''w'';\r\n			$hfld = PFX.''txp_image.''.$fldmod.''h'';\r\n\r\n			$clauseOR = array();\r\n			foreach ($aspects as $ratio) {\r\n				switch($ratio) {\r\n					case ''portrait'':\r\n						$clauseOR[] = $hfld. '' > ''. $wfld;\r\n						break;\r\n					case ''landscape'':\r\n						$clauseOR[] = $wfld. '' > ''. $hfld;\r\n						break;\r\n					case ''square'':\r\n						$clauseOR[] = $hfld. '' = ''. $wfld;\r\n						break;\r\n				}\r\n			}\r\n			if (!empty($clauseOR)) {\r\n				$clause .= '' AND ''.$wfld.'' > 0 AND ''.$hfld.'' > 0 AND (''.implode(" OR ", $clauseOR).'') '';\r\n			}\r\n		}\r\n\r\n		// Filter by author\r\n		if ($author) {\r\n			$authors = do_list($author, $delim);\r\n			$clause .= " AND " . PFX."txp_image.author IN (''".implode("'',''", $authors)."'')";\r\n		}\r\n\r\n		// Filter by extension\r\n		if ($extension) {\r\n			$extensions = do_list($extension, $delim);\r\n			foreach ($extensions as $idx => $ext) {\r\n				$extensions[$idx] = ".".$ext;\r\n			}\r\n			$clause .= " AND " . PFX."txp_image.ext IN (''".implode("'',''", $extensions)."'')";\r\n		}\r\n\r\n		// Filter by dimensions\r\n		if ($dimensions) {\r\n			$dimAllow = array(''='', ''>'', ''<'', ''>='', ''<='');\r\n			$dims = do_list($dimensions, $delim);\r\n\r\n			// Compare fullsize or thumbnail aspect ratios\r\n			$fldmod = ($fullmod) == ''t'' ? ''thumb_'' : '''';\r\n			$wfld = PFX.''txp_image.''.$fldmod.''w'';\r\n			$hfld = PFX.''txp_image.''.$fldmod.''h'';\r\n\r\n			$wdim = $dims[0];\r\n			$hdim = (isset($dims[1])) ? $dims[1] : '''';\r\n\r\n			$wdimval = preg_replace(''/[^0-9]/'', '''', $wdim);\r\n			$hdimval = preg_replace(''/[^0-9]/'', '''', $hdim);\r\n			$wdimop = preg_replace(''/[0-9]/'', '''', $wdim);\r\n			$wdimop = in_array($wdimop, $dimAllow) ? $wdimop : ''='';\r\n			$hdimop = preg_replace(''/[0-9]/'', '''', $hdim);\r\n			$hdimop = in_array($hdimop, $dimAllow) ? $hdimop : ''='';\r\n			$clause .= (($wdim) ? '' AND '' . $wfld . $wdimop . $wdimval : '''') . (($hdim) ? '' AND '' . $hfld . $hdimop . $hdimval : '''');\r\n		}\r\n\r\n		// Convert the sort into a valid sorting hierarchy.\r\n		// Note that random is a special case and the following rules apply:\r\n		//  1) Anything appearing before random is passed to the database query for ordering\r\n		//  2) As soon as random is encountered, the rest of the sort string is ignored.\r\n		//     The resultset will be sorted randomly instead\r\n		//  3) If random appears first in the list, the data is retrieved randomly then the\r\n		//     remaining options sort the resultset\r\n		$clauseSort = array();\r\n		$postOrder = array();\r\n		$rule = "sql";\r\n		$postPrefix = "SORT_";\r\n		$fixedOrder = $randsort = false;\r\n		$sort = do_list($sort, $delim);\r\n		for ($idx = 0; $idx < count($sort); $idx++) {\r\n			$sort_dir = explode('' '', $sort[$idx]);\r\n			$direction = $colRef = '''';\r\n			if (count($sort_dir) <= 1) {\r\n				$sort_dir[1] = "asc";\r\n			}\r\n			if ($rule == "sql") {\r\n				$direction = ($sort_dir[1] == "desc") ? '' desc'' : '' asc'';\r\n			} else {\r\n				$direction = ($sort_dir[1] == "desc") ? $postPrefix.''DESC'' : $postPrefix.''ASC'';\r\n			}\r\n\r\n			switch ($sort_dir[0]) {\r\n				case ''random'':\r\n					$rule = "post";\r\n					if ($idx == 0) {\r\n						$clauseSort[] = ''rand()'';\r\n					} else {\r\n						$randsort = true;\r\n					}\r\n					break;\r\n				case ''fixed'':\r\n					// ''fixed'' only works on ids or categories\r\n					$fixedOrder = ($id == '''' && $category == '''') ? false : true;\r\n					break;\r\n				default:\r\n					$colRef = array_search($sort_dir[0], $fieldMap);\r\n					$colName = $fieldMap[$colRef];\r\n					break;\r\n			}\r\n			if ($rule == "sql") {\r\n				if ($colRef != '''') {\r\n					$clauseSort[] = $colRef.$direction;\r\n				}\r\n			} else {\r\n				if (!$randsort) {\r\n					if ($colRef != '''') {\r\n						$postOrder[] = array("col" => $colName, "sort" => $direction);\r\n					}\r\n				}\r\n			}\r\n		}\r\n\r\n		if (count($clauseSort) > 0) {\r\n			$clause .= '' ORDER BY '' . implode(",",$clauseSort);\r\n		}\r\n		if ($debug > 0) {\r\n			echo "++ WHERE CLAUSE ++";\r\n			dmp($clause);\r\n		}\r\n		// The database fields required\r\n		$fields = implode(",", array_keys($fieldMap));\r\n		$cTitle = (isset($l10n_language[''long''])) ? _l10n_make_field_name(''title'', $thislang) : ''title'';\r\n\r\n		$rs = getRows("\r\n			SELECT DISTINCT $fields\r\n			FROM ".PFX."txp_image\r\n			WHERE $clause\r\n			LIMIT 0, $maxlimit\r\n		", $debug);\r\n		list($allImages, $numPages, $thumPage, $offset) = smd_getPage($rs, $gallID, $limit, $offset, $paging);\r\n	}\r\n\r\n\r\n	// Is post-ordering required? Do it\r\n	if ($randsort) {\r\n		shuffle($rs);\r\n	} else if (count($postOrder) > 0) {\r\n		// Translate the rows into columns that can be sorted\r\n		foreach($rs as $key => $row) {\r\n			foreach ($row as $identifier => $item) {\r\n				$varname = "col_".$identifier;\r\n				${$varname}[$key] = $item;\r\n			}\r\n		}\r\n\r\n		// Make up an array_multisort arg list and execute it.\r\n		// The necessary evil() is because we don''t know how many cols there will be in the array\r\n		for ($idx = 0; $idx < count($postOrder); $idx++) {\r\n			$sortargs[] = ''$col_''.$postOrder[$idx][''col''];\r\n			$sortargs[] = $postOrder[$idx][''sort''];\r\n		}\r\n		$sortit = ''array_multisort(''.implode(", ",$sortargs).'', $rs);'';\r\n		eval($sortit);\r\n	}\r\n\r\n	// Is fixed-order output desired? If so, re-order the $rs\r\n	if ($fixedOrder) {\r\n		$orderedRS = Array();\r\n		$ignoreList = Array();\r\n		$remainingRS = Array();\r\n\r\n		// Suck out the fixed items first. IDs then categories\r\n		for ($idx = 0; $idx < count($idinc); $idx++) {\r\n			foreach($rs as $row) {\r\n				if ($id && doQuote($row[''id'']) == $idinc[$idx]) {\r\n					$orderedRS[] = $row;\r\n					$ignoreList[] = $row[''id''];\r\n					break;\r\n				}\r\n			}\r\n		}\r\n		for ($idx = 0; $idx < count($catinc); $idx++) {\r\n			foreach($rs as $row) {\r\n				if ($category && doQuote($row[''category'']) == $catinc[$idx]) {\r\n					$orderedRS[] = $row;\r\n					$ignoreList[] = $row[''id''];\r\n				}\r\n			}\r\n		}\r\n		// Tack on the remaining rows\r\n		if ($rs) {\r\n			foreach($rs as $row) {\r\n				if (!in_array($row[''id''], $ignoreList)) {\r\n					$remainingRS[] = $row;\r\n				}\r\n			}\r\n		}\r\n		$rs = array_merge($orderedRS, $remainingRS);\r\n	}\r\n\r\n	// Grab the portion of the record set actually required to iterate over\r\n	if ($rs) {\r\n		$rs = array_slice($rs, $offset, (($directory) ? $dirlimit : (($limit==0) ? $maxlimit : $limit)));\r\n	}\r\n\r\n	// Prepare fixed thumbsizes\r\n	$thumbWidth = $thumbHeight = '''';\r\n	$thumbWUnits = $thumbHUnits = '''';\r\n	if ($thumbsize) {\r\n		$thumbSizes = do_list($thumbsize, $delim);\r\n		$units = preg_replace(''/[0-9]/'', '''', $thumbSizes[0]);\r\n		if (count($thumbSizes) == 1) {\r\n			$thumbWidth = $thumbHeight = preg_replace(''/[^0-9]/'', '''', $thumbSizes[0]);\r\n			$thumbWUnits = $thumbHUnits = ($units == '''') ? $thumbWUnits : $units;\r\n		} else {\r\n			$thumbWidth = preg_replace(''/[^0-9]/'', '''', $thumbSizes[0]);\r\n			$thumbHeight = preg_replace(''/[^0-9]/'', '''', $thumbSizes[1]);\r\n			$thumbWUnits = ($units == '''' && $thumbWidth != '''') ? $thumbWUnits : $units;\r\n			$units = preg_replace(''/[0-9]/'', '''', $thumbSizes[1]);\r\n			$thumbHUnits = ($units == '''' && $thumbHeight != '''') ? $thumbHUnits : $units;\r\n		}\r\n	}\r\n\r\n	// Prepare fixed image sizes\r\n	$imageWidth = $imageHeight = '''';\r\n	$imageWUnits = $imageHUnits = '''';\r\n	if ($imgsize) {\r\n		$imageSizes = do_list($imgsize, $delim);\r\n		$units = preg_replace(''/[0-9]/'', '''', $imageSizes[0]);\r\n		if (count($imageSizes) == 1) {\r\n			$imageWidth = $imageHeight = preg_replace(''/[^0-9]/'', '''', $imageSizes[0]);\r\n			$imageWUnits = $imageHUnits = ($units == '''') ? $imageWUnits : $units;\r\n		} else {\r\n			$imageWidth = preg_replace(''/[^0-9]/'', '''', $imageSizes[0]);\r\n			$imageHeight = preg_replace(''/[^0-9]/'', '''', $imageSizes[1]);\r\n			$imageWUnits = ($units == '''' && $imageWidth != '''') ? $imageWUnits : $units;\r\n			$units = preg_replace(''/[0-9]/'', '''', $imageSizes[1]);\r\n			$imageHUnits = ($units == '''' && $imageHeight != '''') ? $imageHUnits : $units;\r\n		}\r\n	}\r\n\r\n	// Collation defers parsing the form for each returned item.\r\n	// Instead it outputs the data as a list at the end (items may be individually quoted or extracted if required)\r\n	//TODO : Add ALWAYS switch as a shortcut to specify that all matching items (regardless of #1, #2, #3 etc) are to be quoted\r\n	if ($collate) {\r\n		$dblQuote = $sglQuote = array(''SMDNONE'');\r\n		$collateOpts = do_list($collate, $delim);\r\n		foreach ($collateOpts as $option) {\r\n			$indexes = do_list($option, $paramdelim);\r\n			$colType = array_shift($indexes);\r\n			// Supplying ''quote'' on its own will empty the relevant array, implying "ALL" fields are to be quoted\r\n			switch ($colType) {\r\n				case "sglquote":\r\n					$sglQuote = $indexes;\r\n					break;\r\n				case "dblquote":\r\n				case "quote":\r\n					$dblQuote = $indexes;\r\n					break;\r\n			}\r\n		}\r\n	}\r\n\r\n	// Combos are user-defined combinations of replacement modifiers (e.g. idext: {id}{ext}, ...\r\n	$combos = array();\r\n	if ($combo) {\r\n		$combo = do_list($combo, $delim);\r\n		foreach ($combo as $newDef) {\r\n			list($defParts, $unwanted) = smd_doList($newDef, false, '''', false, $paramdelim, $spc);\r\n			$defName = "{".array_shift($defParts)."}";\r\n			$combos[$defName] = $defParts[0];\r\n		}\r\n	}\r\n	if ($debug > 1) {\r\n		echo "++ COMBOs ++";\r\n		dmp($combos);\r\n	}\r\n	// Construct the output\r\n	$thumbout = array();\r\n	$groupout = array();\r\n	$navout = array();\r\n	$out = array();\r\n	$collations = array();\r\n	$chIdentifier = "onchange";\r\n\r\n	if ($debug > 2) {\r\n		echo "++ RECORD SET ++";\r\n		dmp($rs);\r\n	}\r\n	if ($debug > 3) {\r\n		echo "++ AVAILABLE ''?'' VARS ++";\r\n		if ($pretext) dmp($pretext);\r\n		if ($thisarticle) dmp($thisarticle);\r\n	}\r\n\r\n	if($rs) {\r\n		$idx = 0;\r\n		$ctr = 1;\r\n		$thumbIdx = 0;\r\n		$thumbCtr = 1;\r\n		$numImages = count($rs);\r\n		$repagements = array();\r\n\r\n		// Set up the paging features if required\r\n		if (($limit > 0) && $paging) {\r\n			$next = ($numPages > 1 && $thumPage != $numPages);\r\n			$prev = ($numPages > 1 && $thumPage > 1);\r\n			$prevPage = smd_addQSVar($pretext[''request_uri''], $gallID, $thumPage-1);\r\n			$nextPage = smd_addQSVar($pretext[''request_uri''], $gallID, $thumPage+1);\r\n			$thisPage = smd_addQSVar($pretext[''request_uri''], $gallID, $thumPage);\r\n			$firstPage = smd_addQSVar($pretext[''request_uri''], $gallID, 1);\r\n			$lastPage = smd_addQSVar($pretext[''request_uri''], $gallID, $numPages);\r\n\r\n			// Labels can be specified in the replacement tag after a paramdelim.\r\n			// ''earlier'' and ''later'' don''t have labels. Anything specified after paramdelim is the max number of pages to show; empty = all pages\r\n			$label = array(\r\n					''{navfirst}'' => $smd_gallLang->gTxt(''navfirst''),\r\n					''{navprev}'' => $smd_gallLang->gTxt(''navprev''),\r\n					''{navearlier}'' => '''',\r\n					''{navthis}'' => $thumPage,\r\n					''{navthisurl}'' => $thumPage,\r\n					''{navlater}'' => '''',\r\n					''{navnext}'' => $smd_gallLang->gTxt(''navnext''),\r\n					''{navlast}'' => $smd_gallLang->gTxt(''navlast''),\r\n				);\r\n\r\n			$labelRE = ''/\\{(nav[a-z]+)''.$paramdelim.''(.+?)\\}/'';\r\n			$numLabels = preg_match_all($labelRE, $pageform, $labels);\r\n\r\n			for ($labelCtr = 0; $labelCtr < $numLabels; $labelCtr++) {\r\n				$fieldname = ''{''.$labels[1][$labelCtr].''}'';\r\n				$label[$fieldname] = $labels[2][$labelCtr];\r\n				$pageform = str_replace($labels[0][$labelCtr], ''{''.$labels[1][$labelCtr].''}'', $pageform);\r\n			}\r\n\r\n			if ($debug > 1) {\r\n				echo "++ PAGING LABELS ++";\r\n				dmp($label);\r\n			}\r\n\r\n			// Generate ''earlier'' and ''later'' lists\r\n			$numEarlier = intval($label[''{navearlier}'']);\r\n			$numLater = intval($label[''{navlater}'']);\r\n			$numEarlier = ($numEarlier && ($thumPage-$numEarlier > 1)) ? $numEarlier : $thumPage-1;\r\n			$numLater = ($numLater && ($thumPage+$numLater < $numPages)) ? $numLater : $numPages-$thumPage;\r\n			$navEarlier = $navLater = array();\r\n			for ($cod = $thumPage-$numEarlier; $cod < $thumPage; $cod++) {\r\n				$navEarlier[] = ''<a href="''.smd_addQSVar($pretext[''request_uri''], $gallID, $cod).''">''.$cod.''</a>'';\r\n			}\r\n			for ($cod = $thumPage+1; $cod <= $thumPage+$numLater; $cod++) {\r\n				$navLater[] = ''<a href="''.smd_addQSVar($pretext[''request_uri''], $gallID, $cod).''">''.$cod.''</a>'';\r\n			}\r\n			$navEarlier = doWrap($navEarlier, '''', $navbreak, '''');\r\n			$navLater = doWrap($navLater, '''', $navbreak, '''');\r\n\r\n			// Make up the repagements array for pageform\r\n			$repagements = array(\r\n					''{navfirst}'' => (($prev) ? doTag(''<a href="''.$firstPage.''">''.$label[''{navfirst}''].''</a>'', $navbreak) : doTag($label[''{navfirst}''], $navbreak, $emptyclass)),\r\n					''{navprev}'' => (($prev) ? doTag(''<a href="''.$prevPage.''">''.$label[''{navprev}''].''</a>'', $navbreak) : doTag($label[''{navprev}''], $navbreak, $emptyclass)),\r\n					''{navearlier}'' => $navEarlier,\r\n					''{navthis}'' => doTag($label[''{navthis}''], $navbreak, ''current ''.$emptyclass),\r\n					''{navthisurl}'' => doTag(''<a href="''.$thisPage.''">''.$label[''{navthisurl}''].''</a>'', $navbreak, ''current''),\r\n					''{navlater}'' => $navLater,\r\n					''{navnext}'' => (($next) ? doTag(''<a href="''.$nextPage.''">''.$label[''{navnext}''].''</a>'', $navbreak) : doTag($label[''{navnext}''], $navbreak, $emptyclass)),\r\n					''{navlast}'' => (($next) ? doTag(''<a href="''.$lastPage.''">''.$label[''{navlast}''].''</a>'', $navbreak) : doTag($label[''{navlast}''], $navbreak, $emptyclass)),\r\n					''{navthispageurl}'' => $thisPage,\r\n					''{navnextpageurl}'' => (($next) ? $nextPage : ''''),\r\n					''{navprevpageurl}'' => (($prev) ? $prevPage : ''''),\r\n					''{navfirstpageurl}'' => $firstPage,\r\n					''{navlastpageurl}'' => $lastPage,\r\n					''{totalimages}'' => $numImages,\r\n					''{totalpages}'' => $numPages,\r\n					''{maximages}'' => $allImages,\r\n				);\r\n\r\n				// Perform escaping here, just prior to output\r\n				if ($escapes) {\r\n					foreach ($repagements as $defName => $defVal) {\r\n						if (in_array(trim($defName, "{}"), $escapes)) {\r\n							$repagements[$defName] = htmlspecialchars($defVal, ENT_QUOTES);\r\n						}\r\n					}\r\n				}\r\n\r\n				// Perform the substitutions\r\n				$navout[] = parse(strtr($pageform, $repagements));\r\n		}\r\n\r\n		// For every image in the gallery...\r\n		foreach($rs as $key => $row) {\r\n			if ($directory) {\r\n				$urlPath = $row[''path''].''/'';\r\n			} else {\r\n				$urlPath = hu.$img_dir.''/'';\r\n				$row[''category_title''] = fetch_category_title($row[''category''], ''image'');\r\n			}\r\n			$theThumb = $urlPath . $row[''id''] . $thumbmod . $row[''ext''];\r\n			$fullSize = $urlPath . $row[''id''] . $fullmod .$row[''ext''];\r\n\r\n			// Do we need to reset the thumb counter this time?\r\n			foreach ($resetOpts as $resetOpt => $resetVal) {\r\n				if (strpos($resetOpt, ''smd_every'') === 0) {\r\n					$iter = explode($paramdelim, $resetOpt);\r\n					$iter = (count($iter) > 1) ? $iter[1] : 0;\r\n					if ($thumbIdx % $iter == 0) {\r\n						$thumbIdx = 0;\r\n						$thumbCtr = 1;\r\n					}\r\n				} else if ($row[$resetOpt] != $resetVal) {\r\n					$thumbIdx = 0;\r\n					$thumbCtr = 1;\r\n					break;\r\n				}\r\n			}\r\n\r\n			// Override thumbsize / imgsize if required\r\n			if ($thumbsize) {\r\n				$theThumbWidth = empty($thumbWidth) ? $row[''thumb_w''] : $thumbWidth;\r\n				$theThumbHeight = empty($thumbHeight) ? $row[''thumb_h''] : $thumbHeight;\r\n				$theThumbWUnits = $thumbWUnits;\r\n				$theThumbHUnits = $thumbHUnits;\r\n			} else {\r\n				$theThumbWidth = $row[''thumb_w''];\r\n				$theThumbHeight = $row[''thumb_h''];\r\n				$theThumbWUnits = $theThumbHUnits = '''';\r\n			}\r\n\r\n			if ($imgsize) {\r\n				$theImageWidth = empty($imageWidth) ? $row[''w''] : $imageWidth;\r\n				$theImageHeight = empty($imageHeight) ? $row[''h''] : $imageHeight;\r\n				$theImageWUnits = $imageWUnits;\r\n				$theImageHUnits = $imageHUnits;\r\n			} else {\r\n				$theImageWidth = $row[''w''];\r\n				$theImageHeight = $row[''h''];\r\n				$theImageWUnits = $theImageHUnits = '''';\r\n			}\r\n\r\n			// Construct the replacement array\r\n			$replacements = array(\r\n					''{id}'' => $row[''id''],\r\n					''{name}'' => $row[''name''],\r\n					''{ext}'' => $row[''ext''],\r\n					''{category}'' => $row[''category''],\r\n					''{category_title}'' => ((isset($row[''category_title'']) && $row[''category_title''] !== false) ? $row[''category_title''] : ''''),\r\n					''{author}'' => $row[''author''],\r\n					''{hasthumb}'' => (isset($row[''thumbnail'']) ? $row[''thumbnail''] : 0),\r\n					''{url}'' => $fullSize,\r\n					''{thumburl}'' => $theThumb,\r\n					''{imagepath}'' => $urlPath,\r\n					''{imagedef}'' => basename($fullSize),\r\n					''{thumbdef}'' => basename($theThumb),\r\n					''{alt}'' => $row[''alt''],\r\n					''{title}'' => $row[''caption''],\r\n					''{caption}'' => $row[''caption''],\r\n					''{thumbwidth}'' => $theThumbWidth,\r\n					''{thumbheight}'' => $theThumbHeight,\r\n					''{thumbwunits}'' => $theThumbWUnits,\r\n					''{thumbhunits}'' => $theThumbHUnits,\r\n					''{width}'' => $theImageWidth,\r\n					''{height}'' => $theImageHeight,\r\n					''{wunits}'' => $theImageWUnits,\r\n					''{hunits}'' => $theImageHUnits,\r\n					''{date}'' => (($format) ? safe_strftime($format, strtotime($row[''date'']), $gmt, $lang) : $row[''date'']),\r\n					''{galleryid}'' => $gallID,\r\n					''{index}'' => $idx,\r\n					''{counter}'' => $ctr,\r\n					''{thumbindex}'' => $thumbIdx,\r\n					''{thumbcounter}'' => $thumbCtr,\r\n					''{totalimages}'' => $numImages,\r\n					''{maximages}'' => $allImages,\r\n				);\r\n\r\n			// Add any custom columns\r\n			foreach ($extraMap as $eCol => $eVal) {\r\n				$replacements[''{''.$eVal.''}''] = $row[$eCol];\r\n			}\r\n\r\n			// Append any changed items that are required\r\n			$endtag = false;\r\n			foreach ($resetOpts as $resetOpt => $resetVal) {\r\n				if (strpos($resetOpt, ''smd_every'') === 0) {\r\n					$iter = explode($paramdelim, $resetOpt);\r\n					$iter = (count($iter) > 1) ? $iter[1] : 0;\r\n					if ($thumbCtr % $iter === 0 || $ctr == $numImages) {\r\n						$endtag = true;\r\n					}\r\n				} else {\r\n					$replacements[''{''.$chIdentifier.$paramdelim.$resetOpt.''}''] = ($thumbIdx == 0) ? ((isset($resetForms[$resetOpt])) ? strtr(parse_form($resetForms[$resetOpt]), $replacements) : doTag($row[$resetOpt], $onchangewraptag, $onchangeclass)) : '''';\r\n					// Do a sneaky (yuk) lookahead. Would love to find a better way to do this\r\n					if (isset($rs[$key+1])) {\r\n						// category_title is not available on the next row (yet) so populate it if required... bleurgh!\r\n						if ($resetOpt == ''category_title'') {\r\n							$rs[$key+1][$resetOpt] = (isset($rs[$key+1][''category'']) && !empty($rs[$key+1][''category''])) ? fetch_category_title($rs[$key+1][''category''], ''image'') : '''';\r\n						}\r\n						if(isset($rs[$key+1][$resetOpt]) && ($rs[$key+1][$resetOpt] != $row[$resetOpt])) {\r\n							$endtag = true;\r\n						}\r\n					} else {\r\n						$endtag = true; // Last image\r\n					}\r\n				}\r\n			}\r\n\r\n			$replacements[''{grouptagend}''] = ($grouptag && $endtag) ? ''</'' . $grouptag . ''>'' : '''';\r\n			$replacements[''{grouptagstart}''] = ($grouptag && $thumbIdx == 0) ? ''<'' . $grouptag . (($groupclass) ? '' class="''.$groupclass.''"'' : '''') . ''>'' : '''';\r\n\r\n			// Append any combos\r\n			foreach ($combos as $defName => $newDef) {\r\n				if (!array_key_exists($defName, $replacements)) {\r\n					$replacements[$defName] = strtr($newDef, $replacements);\r\n				}\r\n			}\r\n\r\n			// Solos are items in the output form that require details from a specific row. Useful only in collation\r\n			// mode, they are added to the replacements array on an as-needed basis to save space/time\r\n			$soloRE = ''/\\{([a-z0-9]+)#''.$ctr.''\\}/'';\r\n			$numSolos = preg_match_all($soloRE, $thing, $solos);\r\n			for ($soloCtr = 0; $soloCtr < $numSolos; $soloCtr++) {\r\n				$fieldname = ''{''.$solos[1][$soloCtr].''#''.$ctr.''}'';\r\n				$grabfield = ''{''.$solos[1][$soloCtr].''}'';\r\n				$replacements[$fieldname] = $replacements[$grabfield];\r\n			}\r\n\r\n			// Perform escaping on the replacements.\r\n			// Note the repagements have already been escaped so when/if they are combined with the replacements,\r\n			// the escaping is not done twice\r\n			if ($escapes) {\r\n				foreach ($replacements as $defName => $defVal) {\r\n					if (in_array(trim($d